import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('employees');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    dateOfBirth: '',
    dateOfJoining: '',
    availableLeaves: '',
    department: '',
    position: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to fetch users');
    }
  };

  const handleEdit = (user) => {
    if (!user.hasProfile) {
      setMessage('User has not created their profile yet');
      return;
    }
    setSelectedUser(user);
    setFormData({
      name: user.profile.name,
      email: user.profile.email,
      dateOfBirth: user.profile.dateOfBirth?.split('T')[0] || '',
      dateOfJoining: user.profile.dateOfJoining?.split('T')[0] || '',
      availableLeaves: user.profile.availableLeaves,
      department: user.profile.department || '',
      position: user.profile.position || ''
    });
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5000/api/admin/employees/${selectedUser.profile._id}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage('Employee updated successfully');
      setIsEditing(false);
      setSelectedUser(null);
      fetchUsers();
    } catch (error) {
      setMessage(error.response?.data?.message || 'Update failed');
    }
  };

  const handleDelete = async (user) => {
    if (!user.hasProfile) {
      setMessage('Cannot delete user without profile');
      return;
    }
    if (window.confirm('Are you sure you want to delete this employee profile?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/admin/employees/${user.profile._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setMessage('Employee profile deleted successfully');
        fetchUsers();
      } catch (error) {
        setMessage(error.response?.data?.message || 'Delete failed');
      }
    }
  };

  if (isEditing) {
    return (
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        minHeight: '100vh',
        padding: '2rem'
      }}>
        <div style={{
          backgroundColor: 'white',
          borderRadius: '1rem',
          padding: '2rem',
          maxWidth: '600px',
          margin: '0 auto',
          boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)'
        }}>
          <h2 style={{fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '2rem', textAlign: 'center'}}>
            Edit Employee Profile
          </h2>
          <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', gap: '1.5rem'}}>
            <div>
              <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                Name:
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem',
                  transition: 'border-color 0.2s',
                  outline: 'none'
                }}
                onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                required
              />
            </div>

            <div>
              <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                Email:
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem',
                  transition: 'border-color 0.2s',
                  outline: 'none'
                }}
                onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                required
              />
            </div>

            <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem'}}>
              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                  Date of Birth:
                </label>
                <input
                  type="date"
                  name="dateOfBirth"
                  value={formData.dateOfBirth}
                  onChange={handleChange}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    transition: 'border-color 0.2s',
                    outline: 'none'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                  onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                  required
                />
              </div>

              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                  Date of Joining:
                </label>
                <input
                  type="date"
                  name="dateOfJoining"
                  value={formData.dateOfJoining}
                  onChange={handleChange}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    transition: 'border-color 0.2s',
                    outline: 'none'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                  onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                  required
                />
              </div>
            </div>

            <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '1rem'}}>
              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                  Available Leaves:
                </label>
                <input
                  type="number"
                  name="availableLeaves"
                  value={formData.availableLeaves}
                  onChange={handleChange}
                  min="0"
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    transition: 'border-color 0.2s',
                    outline: 'none'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                  onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                  required
                />
              </div>

              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                  Department:
                </label>
                <input
                  type="text"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    transition: 'border-color 0.2s',
                    outline: 'none'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                  onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                />
              </div>

              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '600', color: '#374151', marginBottom: '0.5rem'}}>
                  Position:
                </label>
                <input
                  type="text"
                  name="position"
                  value={formData.position}
                  onChange={handleChange}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    transition: 'border-color 0.2s',
                    outline: 'none'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                  onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                />
              </div>
            </div>

            <div style={{display: 'flex', gap: '1rem', marginTop: '1rem'}}>
              <button 
                type="submit" 
                style={{
                  flex: 1,
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  padding: '0.875rem 2rem',
                  borderRadius: '0.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'transform 0.2s, box-shadow 0.2s'
                }}
                onMouseOver={(e) => {
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 10px 25px rgba(102, 126, 234, 0.4)';
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = 'none';
                }}
              >
                Update Employee
              </button>
              <button 
                type="button" 
                onClick={() => setIsEditing(false)} 
                style={{
                  flex: 1,
                  backgroundColor: '#6b7280',
                  color: 'white',
                  padding: '0.875rem 2rem',
                  borderRadius: '0.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'background-color 0.2s'
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = '#4b5563'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#6b7280'}
              >
                Cancel
              </button>
            </div>
          </form>
          {message && (
            <div style={{
              marginTop: '1.5rem',
              padding: '1rem',
              backgroundColor: '#d1fae5',
              color: '#065f46',
              borderRadius: '0.5rem',
              textAlign: 'center',
              fontWeight: '500'
            }}>
              {message}
            </div>
          )}
        </div>
      </div>
    );
  }

  const currentUser = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <div style={{
      background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
      minHeight: '100vh'
    }}>
      {/* Modern Sidebar */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        height: '100vh',
        width: '280px',
        background: 'linear-gradient(180deg, #667eea 0%, #764ba2 100%)',
        padding: '2rem 0',
        boxShadow: '4px 0 20px rgba(0, 0, 0, 0.1)',
        zIndex: 1000
      }}>
        <div style={{textAlign: 'center', marginBottom: '3rem'}}>
          <h3 style={{
            color: 'white',
            fontSize: '1.5rem',
            fontWeight: 'bold',
            margin: 0,
            background: 'linear-gradient(45deg, #ffffff, #e0e7ff)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            CzarCore Admin
          </h3>
          <p style={{color: 'rgba(255,255,255,0.8)', fontSize: '0.875rem', margin: '0.5rem 0 0 0'}}>
            Management Dashboard
          </p>
        </div>

        <nav style={{padding: '0 1.5rem'}}>
          {[
            {key: 'dashboard', label: 'Dashboard', icon: 'ðŸ“Š'},
            {key: 'employees', label: 'Employee Management', icon: 'ðŸ‘¥'},
            {key: 'settings', label: 'Settings', icon: 'âš™ï¸'}
          ].map(item => (
            <button
              key={item.key}
              onClick={() => setActiveTab(item.key)}
              style={{
                display: 'flex',
                alignItems: 'center',
                width: '100%',
                padding: '1rem 1.5rem',
                marginBottom: '0.5rem',
                backgroundColor: activeTab === item.key ? 'rgba(255,255,255,0.2)' : 'transparent',
                color: 'white',
                border: 'none',
                borderRadius: '0.75rem',
                fontSize: '1rem',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                backdropFilter: activeTab === item.key ? 'blur(10px)' : 'none'
              }}
              onMouseOver={(e) => {
                if (activeTab !== item.key) {
                  e.target.style.backgroundColor = 'rgba(255,255,255,0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== item.key) {
                  e.target.style.backgroundColor = 'transparent';
                }
              }}
            >
              <span style={{marginRight: '0.75rem', fontSize: '1.25rem'}}>{item.icon}</span>
              {item.label}
            </button>
          ))}

          <button
            onClick={() => {
              localStorage.clear();
              window.location.href = '/';
            }}
            style={{
              display: 'flex',
              alignItems: 'center',
              width: '100%',
              padding: '1rem 1.5rem',
              marginTop: '2rem',
              backgroundColor: 'rgba(239, 68, 68, 0.2)',
              color: 'white',
              border: 'none',
              borderRadius: '0.75rem',
              fontSize: '1rem',
              fontWeight: '500',
              cursor: 'pointer',
              transition: 'all 0.3s ease'
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = 'rgba(239, 68, 68, 0.3)'}
            onMouseOut={(e) => e.target.style.backgroundColor = 'rgba(239, 68, 68, 0.2)'}
          >
            <span style={{marginRight: '0.75rem', fontSize: '1.25rem'}}>ðŸšª</span>
            Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div style={{marginLeft: '280px', padding: '2rem'}}>
        {/* Header */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          backdropFilter: 'blur(10px)',
          borderRadius: '1rem',
          padding: '1.5rem 2rem',
          marginBottom: '2rem',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        }}>
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
            <div>
              <h1 style={{fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', margin: 0}}>
                {activeTab === 'dashboard' ? 'Dashboard Overview' : 
                 activeTab === 'employees' ? 'Employee Management' : 'Settings'}
              </h1>
              <p style={{color: '#6b7280', margin: '0.5rem 0 0 0'}}>
                Welcome back, {currentUser.name || 'Admin'}
              </p>
            </div>
            <div style={{
              display: 'inline-flex',
              alignItems: 'center',
              padding: '0.5rem 1rem',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              borderRadius: '2rem',
              fontSize: '0.875rem',
              fontWeight: '600'
            }}>
              ðŸ‘‘ ADMIN
            </div>
          </div>
        </div>

        {/* Content Area */}
        {activeTab === 'employees' && (
          <div style={{
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(10px)',
            borderRadius: '1rem',
            padding: '2rem',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <div style={{overflowX: 'auto'}}>
              <table style={{width: '100%', borderCollapse: 'separate', borderSpacing: 0}}>
                <thead>
                  <tr style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
                    {['Name', 'Email', 'Status', 'Employee ID', 'Department', 'Joined', 'Actions'].map(header => (
                      <th key={header} style={{
                        padding: '1rem 1.5rem',
                        textAlign: 'left',
                        fontSize: '0.875rem',
                        fontWeight: '600',
                        color: 'white',
                        textTransform: 'uppercase',
                        letterSpacing: '0.05em',
                        borderBottom: 'none',
                        ...(header === 'Name' && {borderTopLeftRadius: '0.75rem'}),
                        ...(header === 'Actions' && {borderTopRightRadius: '0.75rem'})
                      }}>
                        {header}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {users.filter(user => user.role === 'employee').map((user, index) => (
                    <tr key={user._id} style={{
                      backgroundColor: index % 2 === 0 ? 'rgba(255, 255, 255, 0.8)' : 'rgba(249, 250, 251, 0.8)',
                      transition: 'all 0.2s ease'
                    }}
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = 'rgba(102, 126, 234, 0.1)'}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = index % 2 === 0 ? 'rgba(255, 255, 255, 0.8)' : 'rgba(249, 250, 251, 0.8)'}
                    >
                      <td style={{padding: '1rem 1.5rem', fontSize: '0.875rem', fontWeight: '500', color: '#111827'}}>
                        {user.name}
                      </td>
                      <td style={{padding: '1rem 1.5rem', fontSize: '0.875rem', color: '#6b7280'}}>
                        {user.email}
                      </td>
                      <td style={{padding: '1rem 1.5rem'}}>
                        <span style={{
                          display: 'inline-flex',
                          alignItems: 'center',
                          padding: '0.25rem 0.75rem',
                          fontSize: '0.75rem',
                          fontWeight: '600',
                          borderRadius: '1rem',
                          backgroundColor: user.hasProfile ? '#d1fae5' : '#fef3c7',
                          color: user.hasProfile ? '#065f46' : '#92400e'
                        }}>
                          {user.hasProfile ? 'âœ… Complete' : 'â³ Pending'}
                        </span>
                      </td>
                      <td style={{padding: '1rem 1.5rem', fontSize: '0.875rem', color: '#6b7280'}}>
                        {user.profile?.employeeId || 'N/A'}
                      </td>
                      <td style={{padding: '1rem 1.5rem', fontSize: '0.875rem', color: '#6b7280'}}>
                        {user.profile?.department || 'N/A'}
                      </td>
                      <td style={{padding: '1rem 1.5rem', fontSize: '0.875rem', color: '#6b7280'}}>
                        {new Date(user.createdAt).toLocaleDateString()}
                      </td>
                      <td style={{padding: '1rem 1.5rem'}}>
                        {user.hasProfile ? (
                          <div style={{display: 'flex', gap: '0.5rem'}}>
                            <button
                              onClick={() => handleEdit(user)}
                              style={{
                                background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                                color: 'white',
                                padding: '0.5rem 1rem',
                                borderRadius: '0.5rem',
                                fontSize: '0.75rem',
                                fontWeight: '600',
                                border: 'none',
                                cursor: 'pointer',
                                transition: 'transform 0.2s'
                              }}
                              onMouseOver={(e) => e.target.style.transform = 'translateY(-1px)'}
                              onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
                            >
                              âœï¸ Edit
                            </button>
                            <button
                              onClick={() => handleDelete(user)}
                              style={{
                                background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                                color: 'white',
                                padding: '0.5rem 1rem',
                                borderRadius: '0.5rem',
                                fontSize: '0.75rem',
                                fontWeight: '600',
                                border: 'none',
                                cursor: 'pointer',
                                transition: 'transform 0.2s'
                              }}
                              onMouseOver={(e) => e.target.style.transform = 'translateY(-1px)'}
                              onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
                            >
                              ðŸ—‘ï¸ Delete
                            </button>
                          </div>
                        ) : (
                          <span style={{color: '#9ca3af', fontStyle: 'italic', fontSize: '0.875rem'}}>
                            Waiting for profile...
                          </span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>

              {users.filter(user => user.role === 'employee').length === 0 && (
                <div style={{textAlign: 'center', padding: '3rem', color: '#6b7280'}}>
                  <div style={{fontSize: '3rem', marginBottom: '1rem'}}>ðŸ‘¥</div>
                  <p style={{fontSize: '1.125rem', fontWeight: '500'}}>No employees found</p>
                  <p style={{fontSize: '0.875rem'}}>Employees will appear here once they register</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'dashboard' && (
          <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem'}}>
            {[
              {
                title: 'Total Employees',
                value: users.filter(u => u.role === 'employee').length,
                icon: 'ðŸ‘¥',
                gradient: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
              },
              {
                title: 'Complete Profiles',
                value: users.filter(u => u.hasProfile).length,
                icon: 'âœ…',
                gradient: 'linear-gradient(135deg, #10b981 0%, #059669 100%)'
              },
              {
                title: 'Pending Profiles',
                value: users.filter(u => !u.hasProfile && u.role === 'employee').length,
                icon: 'â³',
                gradient: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)'
              }
            ].map((stat, index) => (
              <div key={index} style={{
                background: 'rgba(255, 255, 255, 0.95)',
                backdropFilter: 'blur(10px)',
                borderRadius: '1rem',
                padding: '2rem',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                textAlign: 'center',
                transition: 'transform 0.3s ease'
              }}
              onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
              onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
              >
                <div style={{
                  width: '4rem',
                  height: '4rem',
                  background: stat.gradient,
                  borderRadius: '1rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '1.5rem',
                  margin: '0 auto 1rem auto'
                }}>
                  {stat.icon}
                </div>
                <div style={{fontSize: '2.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem'}}>
                  {stat.value}
                </div>
                <p style={{fontSize: '1rem', fontWeight: '500', color: '#6b7280', margin: 0}}>
                  {stat.title}
                </p>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'settings' && (
          <div style={{
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(10px)',
            borderRadius: '1rem',
            padding: '3rem',
            textAlign: 'center',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <div style={{fontSize: '4rem', marginBottom: '1rem'}}>âš™ï¸</div>
            <h3 style={{fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '1rem'}}>
              Settings Panel
            </h3>
            <p style={{color: '#6b7280', fontSize: '1rem'}}>
              Advanced settings and configurations coming soon...
            </p>
          </div>
        )}
      </div>

      {message && (
        <div style={{
          position: 'fixed',
          bottom: '2rem',
          right: '2rem',
          background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
          color: 'white',
          padding: '1rem 2rem',
          borderRadius: '0.75rem',
          boxShadow: '0 10px 25px rgba(16, 185, 129, 0.4)',
          zIndex: 1000,
          fontWeight: '500'
        }}>
          {message}
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
