import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(true);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      window.location.href = '/auth';
    }

    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        {/* Animated Sidebar */}
        <div className={`${sidebarOpen ? 'w-64' : 'w-16'} ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed transition-all duration-300 ease-in-out z-10`}>
          <div className="p-6">
            <div className="flex items-center justify-between mb-8">
              <h2 className={`text-xl font-bold text-blue-600 transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                CzarCore
              </h2>
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className={`p-2 rounded-lg transition-colors ${
                  theme === 'dark'
                    ? 'hover:bg-gray-700 text-gray-300'
                    : 'hover:bg-gray-100 text-gray-600'
                }`}
              >
                <svg className={`w-5 h-5 transition-transform duration-300 ${sidebarOpen ? 'rotate-0' : 'rotate-180'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
                </svg>
              </button>
            </div>

            <nav className="space-y-2">
              {[
                { id: 'home', icon: 'ðŸ ', label: 'Home', action: () => window.location.href = '/' },
                { id: 'add-employee', icon: 'ðŸ‘¤', label: 'Add Employee' },
                { id: 'employees', icon: 'ðŸ‘¥', label: 'All Employees' },
                { id: 'leave-requests', icon: 'ðŸ“', label: 'Leave Requests' },
                { id: 'reports', icon: 'ðŸ“Š', label: 'Reports' },
                { id: 'analytics', icon: 'ðŸ“ˆ', label: 'Analytics' },
                { id: 'settings', icon: 'âš™ï¸', label: 'Settings' }
              ].map((item) => (
                <button
                  key={item.id}
                  onClick={() => item.action ? item.action() : setActiveTab(item.id)}
                  className={`w-full text-left px-4 py-3 rounded-lg transition-all duration-200 flex items-center gap-3 group ${
                    activeTab === item.id
                      ? 'bg-indigo-600 text-white transform scale-105'
                      : theme === 'dark'
                      ? 'text-gray-300 hover:bg-gray-700 hover:text-white'
                      : 'text-gray-600 hover:bg-gray-100 hover:text-gray-900'
                  }`}
                >
                  <span className="text-xl transition-transform duration-200 group-hover:scale-110">{item.icon}</span>
                  <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                    {item.label}
                  </span>
                </button>
              ))}
            </nav>
          </div>

          <div className="absolute bottom-4 left-4 right-4">
            <button
              onClick={toggleTheme}
              className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center gap-3 ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              <span className="text-xl">{theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}</span>
              <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                {theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
              </span>
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className={`${sidebarOpen ? 'ml-64' : 'ml-16'} flex-1 p-8 transition-all duration-300`}>
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                {activeTab === 'home' && 'ðŸ  Dashboard Home'}
                {activeTab === 'add-employee' && 'ðŸ‘¤ Add New Employee'}
                {activeTab === 'employees' && 'ðŸ‘¥ All Employees'}
                {activeTab === 'leave-requests' && 'ðŸ“ Leave Requests'}
                {activeTab === 'reports' && 'ðŸ“Š Reports'}
                {activeTab === 'analytics' && 'ðŸ“ˆ Analytics'}
                {activeTab === 'settings' && 'âš™ï¸ Settings'}
              </h1>
              <p className="text-gray-500">Welcome back, {user.name}!</p>
            </div>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                window.location.href = '/auth';
              }}
              className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {/* Animated Landing Page Content */}
          {activeTab === 'home' && (
            <div className="space-y-8">
              {/* Stats Cards with Animation */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {[
                  { title: 'Total Employees', value: '248', icon: 'ðŸ‘¥', color: 'bg-blue-500', delay: '0s' },
                  { title: 'Present Today', value: '235', icon: 'âœ…', color: 'bg-blue-600', delay: '0.1s' },
                  { title: 'On Leave', value: '12', icon: 'ðŸ–ï¸', color: 'bg-blue-700', delay: '0.2s' },
                  { title: 'Departments', value: '8', icon: 'ðŸ¢', color: 'bg-blue-800', delay: '0.3s' }
                ].map((stat, index) => (
                  <div
                    key={index}
                    className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-300 animate-fadeInUp`}
                    style={{ animationDelay: stat.delay }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-500 mb-1">{stat.title}</p>
                        <p className="text-3xl font-bold text-gray-800">{stat.value}</p>
                      </div>
                      <div className={`${stat.color} p-3 rounded-full text-white text-2xl`}>
                        {stat.icon}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Animated Welcome Section */}
              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-slideInLeft`}>
                <div className="text-center">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h2 className="text-4xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                        Welcome to CzarCore EMS
                      </h2>
                      <p className="text-xl text-gray-600 mb-6">
                        Your comprehensive Employee Management System for modern workforce management
                      </p>
                      <div className="flex space-x-4">
                        <button
                          onClick={() => setActiveTab('add-employee')}
                          className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors transform hover:scale-105"
                        >
                          Add Employee
                        </button>
                        <button
                          onClick={() => setActiveTab('reports')}
                          className="px-6 py-3 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50 transition-colors transform hover:scale-105"
                        >
                          View Calendar
                        </button>
                      </div>
                    </div>
                    <div className="flex-1 flex justify-center">
                      <div className="relative">
