import React, { useState, useEffect } from 'react';

function AdminPanel() {
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [employees, setEmployees] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    availableLeaves: 20,
    dateOfJoining: '',
    employeeId: '',
    dateOfBirth: '',
    department: '',
    position: '',
    phone: '',
    address: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [holidayForm, setHolidayForm] = useState({
    name: '',
    date: '',
    year: new Date().getFullYear()
  });

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
    fetchEmployees();
    fetchHolidays();
    fetchLeaveRequests();

    const handleStorageChange = (e) => {
      if (e.key === 'theme') {
        setTheme(e.newValue);
        document.documentElement.setAttribute('data-theme', e.newValue);
      }
    };
    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
    window.dispatchEvent(new StorageEvent('storage', { key: 'theme', newValue: newTheme }));
  };

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/employees');
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/leave-requests');
      const data = await response.json();
      setLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedData = {
      ...formData,
      [name]: value
    };

    // Auto-generate employee ID and work email when name changes
    if (name === 'name' && value.trim()) {
      const nameForId = value.toLowerCase().replace(/[^a-z]/g, '');
      const timestamp = Date.now().toString().slice(-4);
      updatedData.employeeId = `EMP${nameForId.slice(0, 3).toUpperCase()}${timestamp}`;
      updatedData.email = `${value.toLowerCase().replace(/\s+/g, '.')}@czarcore.com`;
    }

    setFormData(updatedData);
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};
    const emailRegex = /\S+@\S+\.\S+/;

    if (!formData.name.trim()) errors.name = 'Name is required';
    if (!formData.email.trim()) errors.email = 'Email is required';
    else if (!emailRegex.test(formData.email)) errors.email = 'Email is invalid';
    if (!editingEmployee && !formData.password) errors.password = 'Password is required';
    else if (!editingEmployee && formData.password.length < 6) errors.password = 'Password must be at least 6 characters';
    if (!formData.employeeId.trim()) errors.employeeId = 'Employee ID is required';
    if (!formData.department.trim()) errors.department = 'Department is required';
    if (!formData.position.trim()) errors.position = 'Position is required';
    if (!formData.dateOfJoining) errors.dateOfJoining = 'Date of joining is required';
    if (!formData.dateOfBirth) errors.dateOfBirth = 'Date of birth is required';
    if (formData.availableLeaves < 0 || formData.availableLeaves > 30) errors.availableLeaves = 'Available leaves must be between 0 and 30';

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleHolidayChange = (e) => {
    setHolidayForm({
      ...holidayForm,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const url = editingEmployee 
        ? `http://localhost:5002/api/employees/${editingEmployee._id}`
        : 'http://localhost:5002/api/employees';
      const method = editingEmployee ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        fetchEmployees();
        resetForm();
        alert(`Employee ${editingEmployee ? 'updated' : 'added'} successfully!`);
      } else {
        alert(data.message || 'Error saving employee');
      }
    } catch (error) {
      console.error('Error saving employee:', error);
      alert('Network error. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleHolidaySubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5002/api/holidays', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(holidayForm),
      });

      if (response.ok) {
        fetchHolidays();
        setHolidayForm({ name: '', date: '', year: new Date().getFullYear() });
      }
    } catch (error) {
      console.error('Error saving holiday:', error);
    }
  };

  const handleLeaveAction = async (requestId, action) => {
    if (window.confirm(`Are you sure you want to ${action} this leave request?`)) {
      try {
        const response = await fetch(`http://localhost:5002/api/leave-requests/${requestId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ status: action }),
        });

        if (response.ok) {
          fetchLeaveRequests();
        }
      } catch (error) {
        console.error('Error updating leave request:', error);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      password: '',
      availableLeaves: 20,
      dateOfJoining: '',
      employeeId: '',
      dateOfBirth: '',
      department: '',
      position: '',
      phone: '',
      address: ''
    });
    setFormErrors({});
    setShowAddForm(false);
    setEditingEmployee(null);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setFormData({
      name: employee.name || '',
      email: employee.email || employee.workEmail || employee.personalEmail || '',
      password: '',
      availableLeaves: employee.availableLeaves || 20,
      dateOfJoining: employee.dateOfJoining?.split('T')[0] || '',
      employeeId: employee.employeeId || '',
      dateOfBirth: employee.dateOfBirth?.split('T')[0] || '',
      department: employee.department || '',
      position: employee.position || '',
      phone: employee.phone || '',
      address: employee.address || ''
    });
    setFormErrors({});
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5002/api/employees/${id}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (response.ok) {
          fetchEmployees();
          alert('Employee deleted successfully!');
        } else {
          const data = await response.json();
          alert(data.message || 'Error deleting employee');
        }
      } catch (error) {
        console.error('Error deleting employee:', error);
        alert('Network error. Please try again.');
      }
    }
  };

  const employeesArray = Array.isArray(employees) ? employees : [];
  const holidaysArray = Array.isArray(holidays) ? holidays : [];
  const leaveRequestsArray = Array.isArray(leaveRequests) ? leaveRequests : [];

  const filteredEmployees = departmentFilter 
    ? employeesArray.filter(emp => emp.department && emp.department.toLowerCase().includes(departmentFilter.toLowerCase()))
    : employeesArray;

  const filteredHolidays = holidaysArray.filter(holiday => holiday.year === selectedYear);

