          {activeTab === 'employees' && (
            <div>
              <div className="mb-6">
                <button
                  onClick={() => setShowAddForm(!showAddForm)}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                >
                  {showAddForm ? 'Cancel' : 'Add Employee'}
                </button>
