import React, { useState, useEffect } from 'react';

const CreativeCalendar = ({ holidays = [], onAddHoliday, onDeleteHoliday, theme = 'light' }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [taskType, setTaskType] = useState('reminder');

  const monthGradients = [
    'from-blue-400 to-blue-600', 'from-pink-400 to-red-500', 'from-green-400 to-emerald-500',
    'from-yellow-400 to-orange-500', 'from-emerald-400 to-green-500', 'from-cyan-400 to-blue-500',
    'from-orange-400 to-red-500', 'from-amber-400 to-yellow-500', 'from-orange-400 to-amber-600',
    'from-red-400 to-orange-500', 'from-gray-400 to-slate-500', 'from-red-400 to-green-500'
  ];

  const quotes = [
    "Today is full of possibilities ‚ú®",
    "Make every moment count üí´",
    "Progress over perfection üöÄ",
    "You're doing amazing! üåü"
  ];

  const moodColors = ['bg-green-400', 'bg-blue-400', 'bg-yellow-400', 'bg-purple-400', 'bg-pink-400'];
  const [currentMood, setCurrentMood] = useState(0);

  const getDaysInMonth = () => new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
  const getFirstDay = () => new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();

  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const getEventsForDate = (day) => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    const dateStr = date.toDateString();

    const dayHolidays = holidays.filter(h => new Date(h.date).toDateString() === dateStr);
    const dayTasks = tasks.filter(t => new Date(t.date).toDateString() === dateStr);

    return [...dayHolidays.map(h => ({...h, type: 'holiday'})), ...dayTasks];
  };

  const addTask = () => {
    if (!newTask.trim() || !selectedDate) return;

    const task = {
      id: Date.now(),
      name: newTask,
      date: selectedDate,
      type: taskType,
      priority: taskType === 'urgent' ? 'high' : 'normal'
    };

    setTasks([...tasks, task]);
    setNewTask('');
    setShowModal(false);
  };

  const getUpcomingEvents = () => {
    const today = new Date();
    const upcoming = [];

    holidays.forEach(h => {
      if (new Date(h.date) >= today) {
        upcoming.push({...h, type: 'holiday'});
      }
    });

    tasks.forEach(t => {
      if (new Date(t.date) >= today) {
        upcoming.push(t);
      }
    });

    return upcoming.sort((a, b) => new Date(a.date) - new Date(b.date)).slice(0, 5);
  };

  const getNextHoliday = () => {
    const today = new Date();
    const nextHoliday = holidays
      .filter(h => new Date(h.date) >= today)
      .sort((a, b) => new Date(a.date) - new Date(b.date))[0];

    if (nextHoliday) {
      const days = Math.ceil((new Date(nextHoliday.date) - today) / (1000 * 60 * 60 * 24));
      return { ...nextHoliday, daysLeft: days };
    }
    return null;
  };

  const renderCalendarDays = () => {
    const daysInMonth = getDaysInMonth();
    const firstDay = getFirstDay();
    const days = [];
    const today = new Date();

    // Empty cells
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="h-24"></div>);
    }

    // Days
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
      const events = getEventsForDate(day);
      const isToday = date.toDateString() === today.toDateString();
      const isSelected = selectedDate && date.toDateString() === selectedDate.toDateString();

      days.push(
        <div
          key={day}
          onClick={() => {
            setSelectedDate(date);
            setShowModal(true);
          }}
          className={`h-24 p-2 rounded-2xl cursor-pointer transition-all duration-300 hover:scale-105 hover:shadow-xl ${
            isToday ? 'bg-gradient-to-br from-indigo-500 to-purple-600 text-white shadow-lg' :
            isSelected ? 'bg-white shadow-lg border-2 border-indigo-400' :
            theme === 'dark' ? 'bg-gray-800 hover:bg-gray-700' : 'bg-white/80 hover:bg-white shadow-md'
          }`}
        >
          <div className="font-bold text-sm mb-1">{day}</div>
          <div className="space-y-1">
            {events.slice(0, 2).map((event, idx) => (
              <div
                key={idx}
                className={`text-xs px-2 py-1 rounded-full text-white truncate ${
                  event.type === 'holiday' ? 'bg-green-500' :
                  event.type === 'urgent' ? 'bg-red-500' :
                  event.type === 'reminder' ? 'bg-blue-500' : 'bg-purple-500'
                }`}
              >
                {event.type === 'holiday' ? 'üéâ' : 
                 event.type === 'urgent' ? '‚ö†Ô∏è' : 
                 event.type === 'reminder' ? '‚è∞' : 'üìù'} {event.name}
              </div>
            ))}
            {events.length > 2 && (
              <div className="text-xs text-gray-500">+{events.length - 2}</div>
            )}
          </div>
        </div>
      );
    }

    return days;
  };

  const nextHoliday = getNextHoliday();
  const todayQuote = quotes[new Date().getDate() % quotes.length];

  return (
    <div className={`p-6 rounded-3xl ${theme === 'dark' ? 'bg-gray-900' : 'bg-gradient-to-br from-gray-50 to-white'} shadow-2xl`}>
      <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">

        {/* Main Calendar */}
        <div className="lg:col-span-3">
          {/* Header */}
          <div className={`bg-gradient-to-r ${monthGradients[currentDate.getMonth()]} p-6 rounded-2xl text-white mb-6 shadow-lg`}>
            <div className="flex items-center justify-between">
              <button
                onClick={() => navigateMonth(-1)}
                className="p-3 rounded-full bg-white/20 hover:bg-white/30 transition-all duration-300"
              >
                ‚Üê
              </button>
              <h2 className="text-3xl font-bold">
                {currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
              </h2>
              <button
                onClick={() => navigateMonth(1)}
                className="p-3 rounded-full bg-white/20 hover:bg-white/30 transition-all duration-300"
              >
                ‚Üí
              </button>
            </div>
          </div>

          {/* Days Header */}
          <div className="grid grid-cols-7 gap-4 mb-4">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-semibold text-gray-600 py-2">
                {day}
              </div>
            ))}
          </div>

          {/* Calendar Grid */}
          <div className="grid grid-cols-7 gap-4">
            {renderCalendarDays()}
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Next Holiday Countdown */}
          {nextHoliday && (
            <div className={`p-4 rounded-2xl bg-gradient-to-br from-green-400 to-emerald-500 text-white shadow-lg`}>
              <h3 className="font-bold mb-2">üéâ Next Holiday</h3>
              <p className="text-sm opacity-90">{nextHoliday.name}</p>
              <p className="text-2xl font-bold">{nextHoliday.daysLeft} days</p>
            </div>
          )}

          {/* Upcoming Events */}
          <div className={`p-4 rounded-2xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
            <h3 className="font-bold mb-3 flex items-center gap-2">
              üìÖ Upcoming
            </h3>
            <div className="space-y-2 max-h-48 overflow-y-auto">
              {getUpcomingEvents().map((event, idx) => (
                <div
                  key={idx}
                  className={`p-2 rounded-lg text-sm ${
                    event.type === 'holiday' ? 'bg-green-100 text-green-800' :
                    event.type === 'urgent' ? 'bg-red-100 text-red-800' :
                    'bg-blue-100 text-blue-800'
                  }`}
                >
                  <div className="font-medium">{event.name}</div>
                  <div className="text-xs opacity-75">
                    {new Date(event.date).toLocaleDateString()}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Quote of the Day */}
          <div className={`p-4 rounded-2xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
            <h3 className="font-bold mb-2">üí≠ Daily Inspiration</h3>
            <p className="text-sm italic text-gray-600">{todayQuote}</p>
          </div>

          {/* Mood Bar */}
          <div className={`p-4 rounded-2xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
            <h3 className="font-bold mb-3">üåà Today's Vibe</h3>
            <div className="flex gap-2">
              {moodColors.map((color, idx) => (
                <button
                  key={idx}
                  onClick={() => setCurrentMood(idx)}
                  className={`w-8 h-8 rounded-full ${color} transition-all duration-300 ${
                    currentMood === idx ? 'scale-125 shadow-lg' : 'hover:scale-110'
                  }`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Task Modal */}
      {showModal && selectedDate && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 backdrop-blur-sm">
          <div className={`p-6 rounded-3xl max-w-md w-full mx-4 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-2xl`}>
            <h3 className="text-xl font-bold mb-4">
              üìÖ {selectedDate.toLocaleDateString()}
            </h3>

            {/* Existing Events */}
            <div className="mb-4">
              {getEventsForDate(selectedDate.getDate()).map((event, idx) => (
                <div key={idx} className={`p-2 rounded-lg mb-2 text-sm ${
                  event.type === 'holiday' ? 'bg-green-100 text-green-800' :
                  event.type === 'urgent' ? 'bg-red-100 text-red-800' :
                  'bg-blue-100 text-blue-800'
                }`}>
                  {event.type === 'holiday' ? 'üéâ' : 
                   event.type === 'urgent' ? '‚ö†Ô∏è' : '‚è∞'} {event.name}
                </div>
              ))}
            </div>

            {/* Add New Task */}
            <div className="space-y-3">
              <input
                type="text"
                placeholder="Add new task..."
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                className={`w-full p-3 rounded-xl border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-200'}`}
              />
              <select
                value={taskType}
                onChange={(e) => setTaskType(e.target.value)}
                className={`w-full p-3 rounded-xl border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-200'}`}
              >
                <option value="reminder">‚è∞ Reminder</option>
                <option value="urgent">‚ö†Ô∏è Urgent</option>
                <option value="meeting">üë• Meeting</option>
                <option value="personal">üí≠ Personal</option>
              </select>
              <div className="flex gap-3">
                <button
                  onClick={addTask}
                  className="flex-1 py-3 bg-gradient-to-r from-indigo-500 to-purple-600 text-white rounded-xl font-semibold hover:shadow-lg transition-all duration-300"
                >
                  Add Task
                </button>
                <button
                  onClick={() => setShowModal(false)}
                  className={`flex-1 py-3 rounded-xl font-semibold transition-all duration-300 ${
                    theme === 'dark' ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-200 hover:bg-gray-300'
                  }`}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreativeCalendar;
