import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [employees, setEmployees] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    availableLeaves: '',
    dateOfJoining: '',
    employeeId: '',
    dateOfBirth: '',
    department: ''
  });
  const [departmentFilter, setDepartmentFilter] = useState('');

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      const parsedUser = JSON.parse(userData);
      setUser(parsedUser);

      // Redirect admins to admin panel
      if (parsedUser.role === 'admin') {
        window.location.href = '/admin';
        return;
      }
    } else {
      window.location.href = '/auth';
    }

    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);

    fetchEmployees();
    fetchHolidays();
    fetchLeaveRequests();
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const fetchEmployees = async () => {
    try {
      console.log('Fetching employees...');
      const response = await fetch('http://localhost:5001/api/employees');
      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Employees data:', data);
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/leave-requests');
      const data = await response.json();
      setLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5001/api/employees', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        fetchEmployees();
        setFormData({
          name: '',
          email: '',
          password: '',
          availableLeaves: '',
          dateOfJoining: '',
          employeeId: '',
          dateOfBirth: '',
          department: ''
        });
        alert('Employee added successfully!');
      }
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  const handleLeaveAction = async (requestId, action) => {
    if (window.confirm(`Are you sure you want to ${action} this leave request?`)) {
      try {
        const response = await fetch(`http://localhost:5001/api/leave-requests/${requestId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ status: action }),
        });

        if (response.ok) {
          fetchLeaveRequests();
        }
      } catch (error) {
        console.error('Error updating leave request:', error);
      }
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        {/* Animated Sidebar */}
        <div className={`${sidebarOpen ? 'w-64' : 'w-16'} ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed transition-all duration-300 ease-in-out z-10`}>
          <div className="p-6">
            <div className="flex items-center justify-between mb-8">
              <h2 className={`text-xl font-bold text-blue-600 transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                CzarCore
              </h2>
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className={`p-2 rounded-lg transition-colors ${
                  theme === 'dark'
                    ? 'hover:bg-gray-700 text-gray-300'
                    : 'hover:bg-gray-100 text-gray-600'
                }`}
              >
                <svg className={`w-5 h-5 transition-transform duration-300 ${sidebarOpen ? 'rotate-0' : 'rotate-180'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
                </svg>
              </button>
            </div>

            <nav className="space-y-2">
              {[
                { id: 'home', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z"/></svg>, label: 'Home', action: () => window.location.href = '/' },
                { id: 'employees', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M9 6a3 3 0 11-6 0 3 3 0 016 0zM17 6a3 3 0 11-6 0 3 3 0 016 0zM12.93 17c.046-.327.07-.66.07-1a6.97 6.97 0 00-1.5-4.33A5 5 0 0119 16v1h-6.07zM6 11a5 5 0 015 5v1H1v-1a5 5 0 015-5z"/></svg>, label: 'View Employees' },
                { id: 'reports', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M2 11a1 1 0 011-1h2a1 1 0 011 1v5a1 1 0 01-1 1H3a1 1 0 01-1-1v-5zM8 7a1 1 0 011-1h2a1 1 0 011 1v9a1 1 0 01-1 1H9a1 1 0 01-1-1V7zM14 4a1 1 0 011-1h2a1 1 0 011 1v12a1 1 0 01-1 1h-2a1 1 0 01-1-1V4z"/></svg>, label: 'Reports' },
                { id: 'analytics', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M3 3a1 1 0 000 2v8a2 2 0 002 2h2.586l-1.293 1.293a1 1 0 101.414 1.414L10 15.414l2.293 2.293a1 1 0 001.414-1.414L12.414 15H15a2 2 0 002-2V5a1 1 0 100-2H3zm11.707 4.707a1 1 0 00-1.414-1.414L10 9.586 8.707 8.293a1 1 0 00-1.414 0l-2 2a1 1 0 101.414 1.414L8 10.414l1.293 1.293a1 1 0 001.414 0l4-4z" clipRule="evenodd"/></svg>, label: 'Analytics' },
                { id: 'settings', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M11.49 3.17c-.38-1.56-2.6-1.56-2.98 0a1.532 1.532 0 01-2.286.948c-1.372-.836-2.942.734-2.106 2.106.54.886.061 2.042-.947 2.287-1.561.379-1.561 2.6 0 2.978a1.532 1.532 0 01.947 2.287c-.836 1.372.734 2.942 2.106 2.106a1.532 1.532 0 012.287.947c.379 1.561 2.6 1.561 2.978 0a1.533 1.533 0 012.287-.947c1.372.836 2.942-.734 2.106-2.106a1.533 1.533 0 01.947-2.287c1.561-.379 1.561-2.6 0-2.978a1.532 1.532 0 01-.947-2.287c.836-1.372-.734-2.942-2.106-2.106a1.532 1.532 0 01-2.287-.947zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd"/></svg>, label: 'Settings' }
              ].map((item) => (
                <button
                  key={item.id}
                  onClick={() => item.action ? item.action() : setActiveTab(item.id)}
                  className={`w-full text-left px-4 py-3 rounded-lg transition-all duration-200 flex items-center gap-3 group ${
                    activeTab === item.id
                      ? 'bg-indigo-600 text-white transform scale-105'
                      : theme === 'dark'
                      ? 'text-gray-300 hover:bg-gray-700 hover:text-white'
                      : 'text-gray-600 hover:bg-gray-100 hover:text-gray-900'
                  }`}
                >
                  <span className="transition-transform duration-200 group-hover:scale-110">{item.icon}</span>
                  <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                    {item.label}
                  </span>
                </button>
              ))}
            </nav>
          </div>

          <div className="absolute bottom-4 left-4 right-4">
            <button
              onClick={toggleTheme}
              className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center gap-3 ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              <span className="text-xl">{theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}</span>
              <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                {theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
              </span>
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className={`${sidebarOpen ? 'ml-64' : 'ml-16'} flex-1 p-8 transition-all duration-300`}>
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                {activeTab === 'home' && 'ðŸ  Employee Dashboard'}
                {activeTab === 'employees' && 'ðŸ‘¥ View Employees'}
                {activeTab === 'reports' && 'ðŸ“Š Reports'}
                {activeTab === 'analytics' && 'ðŸ“ˆ Analytics'}
                {activeTab === 'settings' && 'âš™ï¸ Settings'}
              </h1>
              <p className="text-gray-500">Welcome back, {user.name}!</p>
            </div>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                window.location.href = '/auth';
              }}
              className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {/* Animated Landing Page Content */}
          {activeTab === 'home' && (
            <div className="space-y-8">
              {/* Stats Cards with Animation */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {[
                  { title: 'Total Employees', value: employees.length.toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path d="M9 6a3 3 0 11-6 0 3 3 0 016 0zM17 6a3 3 0 11-6 0 3 3 0 016 0zM12.93 17c.046-.327.07-.66.07-1a6.97 6.97 0 00-1.5-4.33A5 5 0 0119 16v1h-6.07zM6 11a5 5 0 015 5v1H1v-1a5 5 0 015-5z"/></svg>, color: 'bg-blue-500', delay: '0s' },
                  { title: 'Present Today', value: employees.length.toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd"/></svg>, color: 'bg-blue-600', delay: '0.1s' },
                  { title: 'On Leave', value: leaveRequests.filter(req => req.status === 'approved').length.toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd"/></svg>, color: 'bg-blue-700', delay: '0.2s' },
                  { title: 'Departments', value: [...new Set(employees.map(emp => emp.department))].length.toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M4 4a2 2 0 012-2h8a2 2 0 012 2v12a1 1 0 110 2h-3a1 1 0 01-1-1v-6a1 1 0 00-1-1H9a1 1 0 00-1 1v6a1 1 0 01-1 1H4a1 1 0 110-2V4zm3 1h2v2H7V5zm2 4H7v2h2V9zm2-4h2v2h-2V5zm2 4h-2v2h2V9z" clipRule="evenodd"/></svg>, color: 'bg-blue-800', delay: '0.3s' }
                ].map((stat, index) => (
                  <div
                    key={index}
                    className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-300 animate-fadeInUp`}
                    style={{ animationDelay: stat.delay }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-500 mb-1">{stat.title}</p>
                        <p className="text-3xl font-bold text-gray-800">{stat.value}</p>
                      </div>
                      <div className={`${stat.color} p-3 rounded-full text-white`}>
                        {stat.icon}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Animated Welcome Section */}
              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-slideInLeft`}>
                <div className="text-center">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h2 className="text-4xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                        Welcome to CzarCore EMS
                      </h2>
                      <p className="text-xl text-gray-600 mb-6">
                        Your comprehensive Employee Management System for modern workforce management
                      </p>
                      <div className="flex space-x-4">
                        {user && user.role === 'admin' && (
                          <button
                            onClick={() => setActiveTab('add-employee')}
                            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors transform hover:scale-105"
                          >
                            Add Employee
                          </button>
                        )}
                        <button
                          onClick={() => setActiveTab('reports')}
                          className="px-6 py-3 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50 transition-colors transform hover:scale-105"
                        >
                          View Calendar
                        </button>
                      </div>
                    </div>
                    <div className="flex-1"></div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Add Employee Tab - Admin Only */}
          {activeTab === 'add-employee' && (
            user && user.role === 'admin' ? (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">Add New Employee</h2>
              <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-6">
                <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="number" name="availableLeaves" placeholder="Available Leaves" value={formData.availableLeaves} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="date" name="dateOfJoining" placeholder="Date of Joining" value={formData.dateOfJoining} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="text" name="employeeId" placeholder="Employee ID" value={formData.employeeId} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="date" name="dateOfBirth" placeholder="Date of Birth" value={formData.dateOfBirth} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <input type="text" name="department" placeholder="Department" value={formData.department} onChange={handleInputChange} required className="p-3 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
                <button type="submit" className="col-span-2 bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition-colors">Add Employee</button>
              </form>
            </div>
            ) : (
              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn text-center`}>
                <h2 className="text-2xl font-semibold mb-4 text-red-600">Access Denied</h2>
                <p className="text-gray-600">Only administrators can add employees.</p>
                <button onClick={() => setActiveTab('home')} className="mt-4 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">Go to Home</button>
              </div>
            )
          )}

          {/* All Employees Tab */}
          {activeTab === 'employees' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn`}>
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-semibold text-blue-600">All Employees</h2>
                <input type="text" placeholder="Filter by department" value={departmentFilter} onChange={(e) => setDepartmentFilter(e.target.value)} className="p-2 border border-blue-200 rounded-lg focus:border-blue-500 outline-none" />
              </div>
              <div className="overflow-x-auto">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-50">
                      <th className="border border-blue-200 p-3 text-left">Name</th>
                      <th className="border border-blue-200 p-3 text-left">Email</th>
                      <th className="border border-blue-200 p-3 text-left">Employee ID</th>
                      <th className="border border-blue-200 p-3 text-left">Department</th>
                      <th className="border border-blue-200 p-3 text-left">Available Leaves</th>
                      <th className="border border-blue-200 p-3 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {employees.length === 0 ? (
                      <tr>
                        <td colSpan="6" className="border border-blue-200 p-3 text-center text-gray-500">
                          No employees found. Add some employees to see them here.
                        </td>
                      </tr>
                    ) : (
                      employees.filter(emp => !departmentFilter || emp.department?.toLowerCase().includes(departmentFilter.toLowerCase())).map((employee) => (
                        <tr key={employee._id}>
                          <td className="border border-blue-200 p-3">{employee.name}</td>
                          <td className="border border-blue-200 p-3">{employee.workEmail || employee.personalEmail || employee.email}</td>
                          <td className="border border-blue-200 p-3">{employee.employeeId}</td>
                          <td className="border border-blue-200 p-3">{employee.department}</td>
                          <td className="border border-blue-200 p-3">{employee.availableLeaves}</td>
                          <td className="border border-blue-200 p-3">
                            <button className="bg-blue-500 text-white px-3 py-1 rounded mr-2 hover:bg-blue-600">Edit</button>
                            <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">Delete</button>
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Leave Requests Tab - Admin Only */}
          {activeTab === 'leave-requests' && user && user.role === 'admin' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">Leave Requests</h2>
              <div className="overflow-x-auto">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-50">
                      <th className="border border-blue-200 p-3 text-left">Employee</th>
                      <th className="border border-blue-200 p-3 text-left">Leave Type</th>
                      <th className="border border-blue-200 p-3 text-left">Start Date</th>
                      <th className="border border-blue-200 p-3 text-left">End Date</th>
                      <th className="border border-blue-200 p-3 text-left">Reason</th>
                      <th className="border border-blue-200 p-3 text-left">Status</th>
                      <th className="border border-blue-200 p-3 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {leaveRequests.map((request) => (
                      <tr key={request._id}>
                        <td className="border border-blue-200 p-3">{request.employeeName}</td>
                        <td className="border border-blue-200 p-3">{request.leaveType}</td>
                        <td className="border border-blue-200 p-3">{new Date(request.startDate).toLocaleDateString()}</td>
                        <td className="border border-blue-200 p-3">{new Date(request.endDate).toLocaleDateString()}</td>
                        <td className="border border-blue-200 p-3">{request.reason}</td>
                        <td className="border border-blue-200 p-3">
                          <span className={`px-2 py-1 rounded text-sm ${
                            request.status === 'approved'
                              ? 'bg-green-100 text-green-800'
                              : request.status === 'rejected'
                              ? 'bg-red-100 text-red-800'
                              : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {request.status}
                          </span>
                        </td>
                        <td className="border border-blue-200 p-3">
                          {request.status === 'pending' && (
                            <>
                              <button onClick={() => handleLeaveAction(request._id, 'approved')} className="bg-green-500 text-white px-3 py-1 rounded mr-2 hover:bg-green-600">Approve</button>
                              <button onClick={() => handleLeaveAction(request._id, 'rejected')} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">Reject</button>
                            </>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Reports Tab */}
          {activeTab === 'reports' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">Holiday Calendar</h2>
              <div className="mb-4">
                <select className="p-2 border border-blue-200 rounded-lg focus:border-blue-500 outline-none mr-4">
                  <option>2024</option>
                  <option>2025</option>
                </select>
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700">Add Holiday</button>
              </div>
              <div className="overflow-x-auto">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-50">
                      <th className="border border-blue-200 p-3 text-left">Holiday Name</th>
                      <th className="border border-blue-200 p-3 text-left">Date</th>
                      <th className="border border-blue-200 p-3 text-left">Day</th>
                      <th className="border border-blue-200 p-3 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {holidays.map((holiday) => (
                      <tr key={holiday._id}>
                        <td className="border border-blue-200 p-3">{holiday.name}</td>
                        <td className="border border-blue-200 p-3">{new Date(holiday.date).toLocaleDateString()}</td>
                        <td className="border border-blue-200 p-3">{new Date(holiday.date).toLocaleDateString('en-US', { weekday: 'long' })}</td>
                        <td className="border border-blue-200 p-3">
                          <button className="bg-blue-500 text-white px-3 py-1 rounded mr-2 hover:bg-blue-600">Edit</button>
                          <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">Delete</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Other Tabs */}
          {(activeTab === 'analytics' || activeTab === 'settings') && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg animate-fadeIn`}>
              <h2 className="text-2xl font-semibold mb-4 text-blue-600">
                {activeTab === 'analytics' && 'Advanced Analytics'}
                {activeTab === 'settings' && 'System Settings'}
              </h2>
              <div className="text-center py-12">
                <div className="text-6xl mb-4">ðŸš§</div>
                <p className="text-lg text-gray-500">Feature coming soon...</p>
              </div>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideInLeft {
          from {
            opacity: 0;
            transform: translateX(-50px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        .animate-fadeInUp {
          animation: fadeInUp 0.6s ease-out forwards;
        }

        .animate-slideInLeft {
          animation: slideInLeft 0.8s ease-out forwards;
        }

        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
}

export default Dashboard;
