import React, { useState } from 'react';

function EnhancedCalendar({ holidays, onAddHoliday, onDeleteHoliday, theme }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [newHoliday, setNewHoliday] = useState({ name: '', date: '' });

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add all days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }

    return days;
  };

  const isHoliday = (date) => {
    if (!date) return false;
    return holidays.some(holiday => {
      const holidayDate = new Date(holiday.date);
      return holidayDate.toDateString() === date.toDateString();
    });
  };

  const isSunday = (date) => {
    return date && date.getDay() === 0;
  };

  const isToday = (date) => {
    if (!date) return false;
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  const getHolidayForDate = (date) => {
    if (!date) return null;
    return holidays.find(holiday => {
      const holidayDate = new Date(holiday.date);
      return holidayDate.toDateString() === date.toDateString();
    });
  };

  const handleDateClick = (date) => {
    if (!date) return;
    setSelectedDate(date);
    const holiday = getHolidayForDate(date);
    if (holiday) {
      if (window.confirm(`Delete holiday "${holiday.name}"?`)) {
        onDeleteHoliday(holiday._id);
      }
    } else {
      setNewHoliday({ name: '', date: date.toISOString().split('T')[0] });
      setShowAddForm(true);
    }
  };

  const handleAddHoliday = (e) => {
    e.preventDefault();
    if (newHoliday.name.trim() && newHoliday.date) {
      onAddHoliday(newHoliday);
      setNewHoliday({ name: '', date: '' });
      setShowAddForm(false);
      setSelectedDate(null);
    }
  };

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(prev.getMonth() + direction);
      return newDate;
    });
  };

  const days = getDaysInMonth(currentDate);

  return (
    <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-xl shadow-lg p-6`}>
      {/* Calendar Header */}
      <div className="flex flex-col sm:flex-row justify-between items-center mb-6 gap-4">
        <div className="flex items-center gap-4">
          <button
            onClick={() => navigateMonth(-1)}
            className={`p-2 rounded-lg transition-colors ${
              theme === 'dark' 
                ? 'hover:bg-gray-700 text-gray-300' 
                : 'hover:bg-gray-100 text-gray-600'
            }`}
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <h2 className="text-xl lg:text-2xl font-bold text-center">
            {monthNames[currentDate.getMonth()]} {currentDate.getFullYear()}
          </h2>

          <button
            onClick={() => navigateMonth(1)}
            className={`p-2 rounded-lg transition-colors ${
              theme === 'dark' 
                ? 'hover:bg-gray-700 text-gray-300' 
                : 'hover:bg-gray-100 text-gray-600'
            }`}
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>

        <button
          onClick={() => setCurrentDate(new Date())}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-sm"
        >
          Today
        </button>
      </div>

      {/* Calendar Grid */}
      <div className="grid grid-cols-7 gap-1 mb-4">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className={`p-2 text-center font-semibold text-sm ${
            theme === 'dark' ? 'text-gray-400' : 'text-gray-600'
          }`}>
            {day}
          </div>
        ))}

        {days.map((date, index) => {
          const holiday = getHolidayForDate(date);
          const isHol = isHoliday(date);
          const isSun = isSunday(date);
          const isCurrentDay = isToday(date);

          return (
            <div
              key={index}
              onClick={() => handleDateClick(date)}
              className={`
                relative p-2 h-12 sm:h-16 cursor-pointer transition-all duration-200 rounded-lg
                ${!date ? 'cursor-default' : 'hover:scale-105'}
                ${isCurrentDay ? 'ring-2 ring-blue-500' : ''}
                ${isHol ? 'bg-red-100 text-red-800 hover:bg-red-200' : 
                  isSun ? 'bg-red-50 text-red-600 hover:bg-red-100' :
                  theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}
              `}
            >
              {date && (
                <>
                  <div className={`text-sm font-medium ${
                    isCurrentDay ? 'text-blue-600 font-bold' : ''
                  }`}>
                    {date.getDate()}
                  </div>

                  {isHol && (
                    <div className="absolute bottom-0 left-0 right-0 text-xs p-1 bg-red-500 text-white rounded-b-lg truncate">
                      {holiday?.name}
                    </div>
                  )}

                  {isSun && !isHol && (
                    <div className="absolute bottom-0 left-0 right-0 text-xs text-center text-red-500">
                      â€¢
                    </div>
                  )}
                </>
              )}
            </div>
          );
        })}
      </div>

      {/* Legend */}
      <div className="flex flex-wrap gap-4 text-sm mb-4">
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-red-100 border border-red-300 rounded"></div>
          <span>Holidays</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 bg-red-50 border border-red-200 rounded"></div>
          <span>Sundays</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 border-2 border-blue-500 rounded"></div>
          <span>Today</span>
        </div>
      </div>

      {/* Holiday Stats */}
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-6">
        <div className={`p-3 rounded-lg text-center ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
          <div className="text-2xl font-bold text-blue-600">{holidays.length}</div>
          <div className="text-xs text-gray-500">Total Holidays</div>
        </div>
        <div className={`p-3 rounded-lg text-center ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
          <div className="text-2xl font-bold text-green-600">
            {holidays.filter(h => new Date(h.date) > new Date()).length}
          </div>
          <div className="text-xs text-gray-500">Upcoming</div>
        </div>
        <div className={`p-3 rounded-lg text-center ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
          <div className="text-2xl font-bold text-orange-600">
            {Math.ceil(getDaysInMonth(currentDate).filter(d => d && d.getDay() === 0).length)}
          </div>
          <div className="text-xs text-gray-500">Sundays</div>
        </div>
        <div className={`p-3 rounded-lg text-center ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
          <div className="text-2xl font-bold text-purple-600">
            {holidays.filter(h => {
              const hDate = new Date(h.date);
              return hDate.getMonth() === currentDate.getMonth() && 
                     hDate.getFullYear() === currentDate.getFullYear();
            }).length}
          </div>
          <div className="text-xs text-gray-500">This Month</div>
        </div>
      </div>

      {/* Add Holiday Form */}
      {showAddForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg p-6 w-full max-w-md`}>
            <h3 className="text-lg font-semibold mb-4">Add Holiday</h3>
            <form onSubmit={handleAddHoliday} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Holiday Name</label>
                <input
                  type="text"
                  value={newHoliday.name}
                  onChange={(e) => setNewHoliday({...newHoliday, name: e.target.value})}
                  className={`w-full p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  placeholder="Enter holiday name"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Date</label>
                <input
                  type="date"
                  value={newHoliday.date}
                  onChange={(e) => setNewHoliday({...newHoliday, date: e.target.value})}
                  className={`w-full p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  required
                />
              </div>
              <div className="flex gap-3">
                <button
                  type="submit"
                  className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Add Holiday
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    setSelectedDate(null);
                    setNewHoliday({ name: '', date: '' });
                  }}
                  className={`flex-1 px-4 py-2 rounded-lg transition-colors ${
                    theme === 'dark' 
                      ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                      : 'bg-gray-300 text-gray-700 hover:bg-gray-400'
                  }`}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Instructions */}
      <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-blue-50'} text-sm`}>
        <p className="text-blue-600 font-medium mb-1">ðŸ’¡ Quick Actions:</p>
        <ul className="text-gray-600 space-y-1">
          <li>â€¢ Click on any date to add a holiday</li>
          <li>â€¢ Click on existing holidays to delete them</li>
          <li>â€¢ Use navigation arrows to browse months</li>
        </ul>
      </div>
    </div>
  );
}

export default EnhancedCalendar;
