import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Pie, Line } from 'react-chartjs-2';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

function Analytics({ employees, leaveRequests, theme }) {
  const [selectedPeriod, setSelectedPeriod] = useState('monthly');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

  // Calculate analytics data
  const getAnalyticsData = () => {
    const employeesArray = Array.isArray(employees) ? employees : [];
    const leaveRequestsArray = Array.isArray(leaveRequests) ? leaveRequests : [];

    // Department-wise employee count
    const departmentStats = employeesArray.reduce((acc, emp) => {
      const dept = emp.department || 'Unknown';
      acc[dept] = (acc[dept] || 0) + 1;
      return acc;
    }, {});

    // Leave usage trends
    const leaveStats = leaveRequestsArray
      .filter(req => req.status === 'approved')
      .reduce((acc, req) => {
        const month = new Date(req.startDate).getMonth();
        acc[month] = (acc[month] || 0) + req.days;
        return acc;
      }, {});

    // Monthly leave requests
    const monthlyRequests = leaveRequestsArray.reduce((acc, req) => {
      const month = new Date(req.createdAt || req.startDate).getMonth();
      acc[month] = (acc[month] || 0) + 1;
      return acc;
    }, {});

    return {
      departmentStats,
      leaveStats,
      monthlyRequests,
      totalEmployees: employeesArray.length,
      totalLeaveRequests: leaveRequestsArray.length,
      approvedRequests: leaveRequestsArray.filter(req => req.status === 'approved').length,
      pendingRequests: leaveRequestsArray.filter(req => req.status === 'pending').length,
    };
  };

  const analyticsData = getAnalyticsData();

  // Chart configurations
  const departmentChartData = {
    labels: Object.keys(analyticsData.departmentStats),
    datasets: [
      {
        label: 'Employees',
        data: Object.values(analyticsData.departmentStats),
        backgroundColor: [
          '#3B82F6', '#EF4444', '#10B981', '#F59E0B',
          '#8B5CF6', '#EC4899', '#06B6D4', '#84CC16'
        ],
        borderWidth: 2,
        borderColor: theme === 'dark' ? '#374151' : '#ffffff',
      },
    ],
  };

  const leaveUsageData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    datasets: [
      {
        label: 'Leave Days Used',
        data: Array.from({ length: 12 }, (_, i) => analyticsData.leaveStats[i] || 0),
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        borderColor: '#3B82F6',
        borderWidth: 2,
        tension: 0.4,
      },
    ],
  };

  const requestTrendsData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    datasets: [
      {
        label: 'Leave Requests',
        data: Array.from({ length: 12 }, (_, i) => analyticsData.monthlyRequests[i] || 0),
        backgroundColor: 'rgba(16, 185, 129, 0.8)',
        borderColor: '#10B981',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: theme === 'dark' ? '#E5E7EB' : '#374151',
        },
      },
      title: {
        display: true,
        color: theme === 'dark' ? '#E5E7EB' : '#374151',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          color: theme === 'dark' ? '#9CA3AF' : '#6B7280',
        },
        grid: {
          color: theme === 'dark' ? '#374151' : '#E5E7EB',
        },
      },
      x: {
        ticks: {
          color: theme === 'dark' ? '#9CA3AF' : '#6B7280',
        },
        grid: {
          color: theme === 'dark' ? '#374151' : '#E5E7EB',
        },
      },
    },
  };

  // Export functions
  const exportToPDF = () => {
    const doc = new jsPDF();

    // Title
    doc.setFontSize(20);
    doc.text('Employee Analytics Report', 20, 20);

    // Date
    doc.setFontSize(12);
    doc.text(`Generated on: ${new Date().toLocaleDateString()}`, 20, 35);

    // Summary stats
    doc.setFontSize(14);
    doc.text('Summary Statistics', 20, 55);

    const summaryData = [
      ['Total Employees', analyticsData.totalEmployees],
      ['Total Leave Requests', analyticsData.totalLeaveRequests],
      ['Approved Requests', analyticsData.approvedRequests],
      ['Pending Requests', analyticsData.pendingRequests],
    ];

    autoTable(doc, {
      startY: 65,
      head: [['Metric', 'Value']],
      body: summaryData,
      theme: 'grid',
    });

    // Department breakdown
    doc.text('Department Breakdown', 20, doc.previousAutoTable.finalY + 20);

    const deptData = Object.entries(analyticsData.departmentStats).map(([dept, count]) => [dept, count]);

    autoTable(doc, {
      startY: doc.previousAutoTable.finalY + 30,
      head: [['Department', 'Employee Count']],
      body: deptData,
      theme: 'grid',
    });

    doc.save('employee-analytics-report.pdf');
  };

  const exportToCSV = () => {
    const csvData = [
      ['Department', 'Employee Count'],
      ...Object.entries(analyticsData.departmentStats),
      [''],
      ['Month', 'Leave Days Used', 'Leave Requests'],
      ...Array.from({ length: 12 }, (_, i) => [
        ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'][i],
        analyticsData.leaveStats[i] || 0,
        analyticsData.monthlyRequests[i] || 0,
      ]),
    ];

    const csvContent = csvData.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'employee-analytics-data.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6">
      {/* Header with Export Options */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h2 className="text-2xl font-bold text-blue-600">Analytics Dashboard</h2>
          <p className="text-gray-500">Employee performance and leave usage insights</p>
        </div>
        <div className="flex gap-2">
          <button
            onClick={exportToPDF}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
            Export PDF
          </button>
          <button
            onClick={exportToCSV}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
            Export CSV
          </button>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {[
          { title: 'Total Employees', value: analyticsData.totalEmployees, color: 'bg-blue-500', icon: 'ðŸ‘¥' },
          { title: 'Leave Requests', value: analyticsData.totalLeaveRequests, color: 'bg-green-500', icon: 'ðŸ“‹' },
          { title: 'Approved Requests', value: analyticsData.approvedRequests, color: 'bg-emerald-500', icon: 'âœ…' },
          { title: 'Pending Requests', value: analyticsData.pendingRequests, color: 'bg-yellow-500', icon: 'â³' },
        ].map((metric, index) => (
          <div key={index} className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg`}>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500 mb-1">{metric.title}</p>
                <p className="text-3xl font-bold">{metric.value}</p>
              </div>
              <div className={`${metric.color} p-3 rounded-full text-white text-2xl`}>
                {metric.icon}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Department Distribution */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg`}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">Department Distribution</h3>
          <div className="h-64">
            <Pie data={departmentChartData} options={{ ...chartOptions, maintainAspectRatio: false }} />
          </div>
        </div>

        {/* Leave Usage Trends */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg`}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">Monthly Leave Usage</h3>
          <div className="h-64">
            <Line data={leaveUsageData} options={{ ...chartOptions, maintainAspectRatio: false }} />
          </div>
        </div>

        {/* Request Trends */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg lg:col-span-2`}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">Leave Request Trends</h3>
          <div className="h-64">
            <Bar data={requestTrendsData} options={{ ...chartOptions, maintainAspectRatio: false }} />
          </div>
        </div>
      </div>

      {/* Detailed Tables */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Department Stats Table */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg`}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">Department Statistics</h3>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                  <th className="px-4 py-2 text-left">Department</th>
                  <th className="px-4 py-2 text-left">Employees</th>
                  <th className="px-4 py-2 text-left">Percentage</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(analyticsData.departmentStats).map(([dept, count]) => (
                  <tr key={dept} className={`border-t ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'}`}>
                    <td className="px-4 py-2">{dept}</td>
                    <td className="px-4 py-2">{count}</td>
                    <td className="px-4 py-2">{((count / analyticsData.totalEmployees) * 100).toFixed(1)}%</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Performance Insights */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg`}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">Key Insights</h3>
          <div className="space-y-4">
            <div className="flex items-center gap-3">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span className="text-sm">
                {((analyticsData.approvedRequests / analyticsData.totalLeaveRequests) * 100).toFixed(1)}% approval rate
              </span>
            </div>
            <div className="flex items-center gap-3">
              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
              <span className="text-sm">
                Average {(Object.values(analyticsData.leaveStats).reduce((a, b) => a + b, 0) / analyticsData.totalEmployees).toFixed(1)} leave days per employee
              </span>
            </div>
            <div className="flex items-center gap-3">
              <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
              <span className="text-sm">
                {Object.keys(analyticsData.departmentStats).length} active departments
              </span>
            </div>
            <div className="flex items-center gap-3">
              <div className="w-2 h-2 bg-yellow-500 rounded-full"></div>
              <span className="text-sm">
                {analyticsData.pendingRequests} requests awaiting approval
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
