import React, { useState, useEffect } from 'react';

function AdminPanel() {
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('employees');
  const [employees, setEmployees] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    availableLeaves: 20,
    dateOfJoining: '',
    employeeId: '',
    dateOfBirth: '',
    department: '',
    position: '',
    phone: '',
    address: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [holidayForm, setHolidayForm] = useState({
    name: '',
    date: '',
    year: new Date().getFullYear()
  });

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
    fetchEmployees();
    fetchHolidays();
    fetchLeaveRequests();

    // Listen for theme changes
    const handleStorageChange = (e) => {
      if (e.key === 'theme') {
        setTheme(e.newValue);
        document.documentElement.setAttribute('data-theme', e.newValue);
      }
    };
    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
    window.dispatchEvent(new StorageEvent('storage', { key: 'theme', newValue: newTheme }));
  };

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/employees');
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/leave-requests');
      const data = await response.json();
      setLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    // Clear error when user starts typing
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) errors.name = 'Name is required';
    if (!formData.email.trim()) errors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) errors.email = 'Email is invalid';
    if (!editingEmployee && !formData.password) errors.password = 'Password is required';
    else if (!editingEmployee && formData.password.length < 6) errors.password = 'Password must be at least 6 characters';
    if (!formData.employeeId.trim()) errors.employeeId = 'Employee ID is required';
    if (!formData.department.trim()) errors.department = 'Department is required';
    if (!formData.position.trim()) errors.position = 'Position is required';
    if (!formData.dateOfJoining) errors.dateOfJoining = 'Date of joining is required';
    if (!formData.dateOfBirth) errors.dateOfBirth = 'Date of birth is required';
    if (formData.availableLeaves < 0 || formData.availableLeaves > 30) errors.availableLeaves = 'Available leaves must be between 0 and 30';

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleHolidayChange = (e) => {
    setHolidayForm({
      ...holidayForm,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const url = editingEmployee 
        ? `http://localhost:5001/api/employees/${editingEmployee._id}`
        : 'http://localhost:5001/api/employees';
      const method = editingEmployee ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        fetchEmployees();
        resetForm();
        alert(`Employee ${editingEmployee ? 'updated' : 'added'} successfully!`);
      } else {
        alert(data.message || 'Error saving employee');
      }
    } catch (error) {
      console.error('Error saving employee:', error);
      alert('Network error. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleHolidaySubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5001/api/holidays', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(holidayForm),
      });

      if (response.ok) {
        fetchHolidays();
        setHolidayForm({ name: '', date: '', year: new Date().getFullYear() });
      }
    } catch (error) {
      console.error('Error saving holiday:', error);
    }
  };

  const handleLeaveAction = async (requestId, action) => {
    if (window.confirm(`Are you sure you want to ${action} this leave request?`)) {
      try {
        const response = await fetch(`http://localhost:5001/api/leave-requests/${requestId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ status: action }),
        });

        if (response.ok) {
          fetchLeaveRequests();
        }
      } catch (error) {
        console.error('Error updating leave request:', error);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      password: '',
      availableLeaves: 20,
      dateOfJoining: '',
      employeeId: '',
      dateOfBirth: '',
      department: '',
      position: '',
      phone: '',
      address: ''
    });
    setFormErrors({});
    setShowAddForm(false);
    setEditingEmployee(null);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setFormData({
      name: employee.name || '',
      email: employee.email || employee.workEmail || employee.personalEmail || '',
      password: '',
      availableLeaves: employee.availableLeaves || 20,
      dateOfJoining: employee.dateOfJoining?.split('T')[0] || '',
      employeeId: employee.employeeId || '',
      dateOfBirth: employee.dateOfBirth?.split('T')[0] || '',
      department: employee.department || '',
      position: employee.position || '',
      phone: employee.phone || '',
      address: employee.address || ''
    });
    setFormErrors({});
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5001/api/employees/${id}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (response.ok) {
          fetchEmployees();
          alert('Employee deleted successfully!');
        } else {
          const data = await response.json();
          alert(data.message || 'Error deleting employee');
        }
      } catch (error) {
        console.error('Error deleting employee:', error);
        alert('Network error. Please try again.');
      }
    }
  };

  const filteredEmployees = departmentFilter 
    ? employees.filter(emp => emp.department.toLowerCase().includes(departmentFilter.toLowerCase()))
    : employees;

  const filteredHolidays = holidays.filter(holiday => holiday.year === selectedYear);

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        <div className={`w-64 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed transition-all duration-300 ease-in-out z-10`}>
          <div className="p-6">
            <div className="flex items-center justify-between mb-8">
              <h2 className="text-xl font-bold text-blue-600">CzarCore Admin</h2>
            </div>
            <nav className="space-y-2">
              <button
                onClick={() => setActiveTab('employees')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'employees'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Employee Management
              </button>
              <button
                onClick={() => setActiveTab('holidays')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'holidays'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Holiday Calendar
              </button>
              <button
                onClick={() => setActiveTab('allEmployees')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'allEmployees'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                All Employees
              </button>
              <button
                onClick={() => setActiveTab('leaveRequests')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'leaveRequests'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Leave Requests
              </button>
            </nav>
          </div>
          <div className="absolute bottom-4 left-4 right-4">
            <button
              onClick={toggleTheme}
              className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center gap-3 ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              <span className="text-xl">{theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}</span>
              <span>Theme</span>
            </button>
          </div>
        </div>

        <div className="ml-64 flex-1 p-8 transition-all duration-300">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                {activeTab === 'employees' && 'ðŸ‘¤ Employee Management'}
                {activeTab === 'holidays' && 'ðŸ“… Holiday Calendar'}
                {activeTab === 'allEmployees' && 'ðŸ‘¥ All Employees'}
                {activeTab === 'leaveRequests' && 'ðŸ“ Leave Requests'}
              </h1>
              <p className="text-gray-500">Admin Dashboard</p>
            </div>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                window.location.href = '/auth';
              }}
              className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {activeTab === 'employees' && (
            <div>
              <div className="mb-6">
                <button
                  onClick={() => setShowAddForm(!showAddForm)}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                >
                  {showAddForm ? 'Cancel' : 'Add Employee'}
                </button>
              </div>

              {showAddForm && (
                <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md mb-6`}>
                  <h3 className="text-lg font-semibold mb-6 text-indigo-600">
                    {editingEmployee ? 'âœï¸ Edit Employee' : 'âž• Add New Employee'}
                  </h3>
                  <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                      <h4 className="text-md font-medium mb-3 text-gray-700">Personal Information</h4>
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium mb-1">Full Name *</label>
                          <input
                            type="text"
                            name="name"
                            placeholder="Enter full name"
                            value={formData.name}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.name ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.name && <p className="text-red-500 text-sm mt-1">{formErrors.name}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Email Address *</label>
                          <input
                            type="email"
                            name="email"
                            placeholder="Enter email address"
                            value={formData.email}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.email ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.email && <p className="text-red-500 text-sm mt-1">{formErrors.email}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Date of Birth *</label>
                          <input
                            type="date"
                            name="dateOfBirth"
                            value={formData.dateOfBirth}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.dateOfBirth ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.dateOfBirth && <p className="text-red-500 text-sm mt-1">{formErrors.dateOfBirth}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Phone Number</label>
                          <input
                            type="tel"
                            name="phone"
                            placeholder="Enter phone number"
                            value={formData.phone}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                        </div>
                      </div>
                    </div>

                    <div>
                      <h4 className="text-md font-medium mb-3 text-gray-700">Employment Information</h4>
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium mb-1">Employee ID *</label>
                          <input
                            type="text"
                            name="employeeId"
                            placeholder="Enter employee ID"
                            value={formData.employeeId}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.employeeId ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.employeeId && <p className="text-red-500 text-sm mt-1">{formErrors.employeeId}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Department *</label>
                          <select
                            name="department"
                            value={formData.department}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.department ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          >
                            <option value="">Select Department</option>
                            <option value="IT">IT</option>
                            <option value="HR">HR</option>
                            <option value="Finance">Finance</option>
                            <option value="Marketing">Marketing</option>
                            <option value="Operations">Operations</option>
                            <option value="Sales">Sales</option>
                            <option value="General">General</option>
                          </select>
                          {formErrors.department && <p className="text-red-500 text-sm mt-1">{formErrors.department}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Position *</label>
                          <input
                            type="text"
                            name="position"
                            placeholder="Enter position/job title"
                            value={formData.position}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.position ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.position && <p className="text-red-500 text-sm mt-1">{formErrors.position}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Date of Joining *</label>
                          <input
                            type="date"
                            name="dateOfJoining"
                            value={formData.dateOfJoining}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.dateOfJoining ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.dateOfJoining && <p className="text-red-500 text-sm mt-1">{formErrors.dateOfJoining}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">Available Leaves *</label>
                          <input
                            type="number"
                            name="availableLeaves"
                            placeholder="20"
                            min="0"
                            max="30"
                            value={formData.availableLeaves}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.availableLeaves ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.availableLeaves && <p className="text-red-500 text-sm mt-1">{formErrors.availableLeaves}</p>}
                        </div>
                        <div>
                          <label className="block text-sm font-medium mb-1">{editingEmployee ? 'New Password (leave blank to keep current)' : 'Password *'}</label>
                          <input
                            type="password"
                            name="password"
                            placeholder={editingEmployee ? 'Leave blank to keep current' : 'Enter password'}
                            value={formData.password}
                            onChange={handleInputChange}
                            className={`w-full p-3 border rounded-lg ${formErrors.password ? 'border-red-500' : theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                          />
                          {formErrors.password && <p className="text-red-500 text-sm mt-1">{formErrors.password}</p>}
                        </div>
                      </div>
                    </div>

                    <div className="flex gap-4 pt-4 border-t">
                      <button
                        type="submit"
                        disabled={isSubmitting}
                        className={`px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors flex items-center gap-2 ${
                          isSubmitting ? 'opacity-50 cursor-not-allowed' : ''
                        }`}
                      >
                        {isSubmitting ? 'â³' : editingEmployee ? 'ðŸ’¾' : 'âž•'}
                        {isSubmitting ? 'Saving...' : editingEmployee ? 'Update Employee' : 'Add Employee'}
                      </button>
                      <button
                        type="button"
                        onClick={resetForm}
                        disabled={isSubmitting}
                        className={`px-6 py-3 rounded-lg transition-colors ${
                          theme === 'dark'
                            ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                            : 'bg-gray-300 text-gray-700 hover:bg-gray-400'
                        } ${isSubmitting ? 'opacity-50 cursor-not-allowed' : ''}`}
                      >
                        âŒ Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
                <table className="w-full">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Email</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Employee ID</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Department</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Position</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Available Leaves</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                    {employees.map((employee) => (
                      <tr key={employee._id}>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.name}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.workEmail || employee.personalEmail || employee.email}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.employeeId}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.department}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.position || 'N/A'}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.availableLeaves}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <button
                            onClick={() => handleEdit(employee)}
                            className="text-indigo-600 hover:text-indigo-900 mr-3"
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleDelete(employee._id)}
                            className="text-red-600 hover:text-red-900"
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'holidays' && (
            <div>
              <div className="mb-6 flex gap-4 items-center">
                <select
                  value={selectedYear}
                  onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                  className={`p-2 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                >
                  {[2023, 2024, 2025, 2026].map(year => (
                    <option key={year} value={year}>{year}</option>
                  ))}
                </select>
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md mb-6`}>
                <h3 className="text-lg font-semibold mb-4">Add Holiday</h3>
                <form onSubmit={handleHolidaySubmit} className="flex gap-4">
                  <input
                    type="text"
                    name="name"
                    placeholder="Holiday Name"
                    value={holidayForm.name}
                    onChange={handleHolidayChange}
                    required
                    className={`p-3 border rounded-lg flex-1 ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  <input
                    type="date"
                    name="date"
                    value={holidayForm.date}
                    onChange={handleHolidayChange}
                    required
                    className={`p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  <input
                    type="number"
                    name="year"
                    value={holidayForm.year}
                    onChange={handleHolidayChange}
                    required
                    className={`p-3 border rounded-lg w-24 ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  <button
                    type="submit"
                    className="px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                  >
                    Add Holiday
                  </button>
                </form>
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
                <table className="w-full">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Holiday Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Year</th>
                    </tr>
                  </thead>
                  <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                    {filteredHolidays.map((holiday) => (
                      <tr key={holiday._id}>
                        <td className="px-6 py-4 whitespace-nowrap">{holiday.name}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{new Date(holiday.date).toLocaleDateString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{holiday.year}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'allEmployees' && (
            <div>
              <div className="mb-6">
                <input
                  type="text"
                  placeholder="Filter by department"
                  value={departmentFilter}
                  onChange={(e) => setDepartmentFilter(e.target.value)}
                  className={`p-3 border rounded-lg w-64 ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                />
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
                <table className="w-full">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Email</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Employee ID</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Department</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Date of Birth</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Date of Joining</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Available Leaves</th>
                    </tr>
                  </thead>
                  <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                    {filteredEmployees.map((employee) => (
                      <tr key={employee._id}>
                        <td className="px-6 py-4 whitespace-nowrap font-medium">{employee.name}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.email}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.employeeId}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{employee.department}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {employee.dateOfBirth ? new Date(employee.dateOfBirth).toLocaleDateString() : 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {employee.dateOfJoining ? new Date(employee.dateOfJoining).toLocaleDateString() : 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                            employee.availableLeaves > 10
                              ? 'bg-green-100 text-green-800'
                              : employee.availableLeaves > 5
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {employee.availableLeaves} days
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'leaveRequests' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
              <table className="w-full">
                <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Employee</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Leave Type</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Start Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">End Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Days</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Reason</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                  {leaveRequests.map((request) => (
                    <tr key={request._id}>
                      <td className="px-6 py-4 whitespace-nowrap font-medium">{request.employeeName}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{request.leaveType}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{new Date(request.startDate).toLocaleDateString()}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{new Date(request.endDate).toLocaleDateString()}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{request.days}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{request.reason}</td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                          request.status === 'approved'
                            ? 'bg-green-100 text-green-800'
                            : request.status === 'rejected'
                            ? 'bg-red-100 text-red-800'
                            : 'bg-yellow-100 text-yellow-800'
                        }`}>
                          {request.status}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {request.status === 'pending' && (
                          <div className="flex gap-2">
                            <button
                              onClick={() => handleLeaveAction(request._id, 'approved')}
                              className="text-green-600 hover:text-green-900"
                            >
                              Approve
                            </button>
                            <button
                              onClick={() => handleLeaveAction(request._id, 'rejected')}
                              className="text-red-600 hover:text-red-900"
                            >
                              Reject
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminPanel;
