import React, { useState, useEffect } from 'react';

function EmployeeDashboard() {
  const [user, setUser] = useState(null);
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [employees, setEmployees] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [myLeaveRequests, setMyLeaveRequests] = useState([]);
  const [employeeProfile, setEmployeeProfile] = useState(null);
  const [employeeSearchQuery, setEmployeeSearchQuery] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [leaveFormData, setLeaveFormData] = useState({
    leaveType: 'casual',
    fromDate: '',
    toDate: '',
    reason: ''
  });

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      const parsedUser = JSON.parse(userData);
      setUser(parsedUser);

      if (parsedUser.role === 'admin') {
        window.location.href = '/admin';
        return;
      }
    } else {
      window.location.href = '/auth';
    }

    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);

    fetchEmployees();
    fetchHolidays();
    fetchEmployeeProfile();
    fetchMyLeaveRequests();
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/employees');
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchEmployeeProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5002/api/employee/profile', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();
      setEmployeeProfile(data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const fetchMyLeaveRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5002/api/employee/my-leave-requests', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();
      setMyLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleLeaveSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5002/api/employee/leave-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...leaveFormData,
          employeeId: employeeProfile?._id
        })
      });

      if (response.ok) {
        alert('Leave request submitted successfully!');
        setLeaveFormData({ leaveType: 'casual', fromDate: '', toDate: '', reason: '' });
        fetchMyLeaveRequests();
      }
    } catch (error) {
      console.error('Error submitting leave request:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        <div className={`${sidebarOpen ? 'w-64' : 'w-16'} ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed transition-all duration-300 ease-in-out z-10`}>
          <div className="p-6">
            <div className="flex items-center justify-between mb-8">
              <h2 className={`text-xl font-bold text-blue-600 transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                CzarCore
              </h2>
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className={`p-2 rounded-lg transition-colors ${
                  theme === 'dark'
                    ? 'hover:bg-gray-700 text-gray-300'
                    : 'hover:bg-gray-100 text-gray-600'
                }`}
              >
                <svg className={`w-5 h-5 transition-transform duration-300 ${sidebarOpen ? 'rotate-0' : 'rotate-180'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
                </svg>
              </button>
            </div>

            <nav className="space-y-2">
              {[
                { id: 'dashboard', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z"/></svg>, label: 'Dashboard' },
                { id: 'profile', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd"/></svg>, label: 'My Profile' },
                { id: 'leave-request', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd"/></svg>, label: 'Leave Request' },
                { id: 'employees', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M13 6a3 3 0 11-6 0 3 3 0 616 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3zM6 8a2 2 0 11-4 0 2 2 0 014 0zM16 18v-3a5.972 5.972 0 00-.75-2.906A3.005 3.005 0 0119 15v3h-3zM4.75 12.094A5.973 5.973 0 004 15v3H1v-3a3 3 0 013.75-2.906z"/></svg>, label: 'View Employees' },
                { id: 'holidays', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M2 11a1 1 0 011-1h2a1 1 0 011 1v5a1 1 0 01-1 1H3a1 1 0 01-1-1v-5zM8 7a1 1 0 011-1h2a1 1 0 011 1v9a1 1 0 01-1 1H9a1 1 0 01-1-1V7zM14 4a1 1 0 011-1h2a1 1 0 011 1v12a1 1 0 01-1 1h-2a1 1 0 01-1-1V4z"/></svg>, label: 'Holiday Calendar' },
                { id: 'settings', icon: <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M11.49 3.17c-.38-1.56-2.6-1.56-2.98 0a1.532 1.532 0 01-2.286.948c-1.372-.836-2.942.734-2.106 2.106.54.886.061 2.042-.947 2.287-1.561.379-1.561 2.6 0 2.978a1.532 1.532 0 01.947 2.287c-.836 1.372.734 2.942 2.106 2.106a1.532 1.532 0 012.287.947c.379 1.561 2.6 1.561 2.978 0a1.533 1.533 0 012.287-.947c1.372.836 2.942-.734 2.106-2.106a1.533 1.533 0 01.947-2.287c1.561-.379 1.561-2.6 0-2.978a1.532 1.532 0 01-.947-2.287c.836-1.372-.734-2.942-2.106-2.106a1.532 1.532 0 01-2.287-.947zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd"/></svg>, label: 'Settings' }
              ].map((item) => (
                <button
                  key={item.id}
                  onClick={() => setActiveTab(item.id)}
                  className={`w-full text-left px-4 py-3 rounded-lg transition-all duration-200 flex items-center gap-3 group ${
                    activeTab === item.id
                      ? 'bg-indigo-600 text-white transform scale-105'
                      : theme === 'dark'
                      ? 'text-gray-300 hover:bg-gray-700 hover:text-white'
                      : 'text-gray-600 hover:bg-gray-100 hover:text-gray-900'
                  }`}
                >
                  <span className="transition-transform duration-200 group-hover:scale-110">{item.icon}</span>
                  <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                    {item.label}
                  </span>
                </button>
              ))}
            </nav>
          </div>

          <div className="absolute bottom-4 left-4 right-4 space-y-2">
            <button
              onClick={() => window.location.href = '/'}
              className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center gap-3 ${
                theme === 'dark'
                  ? 'bg-blue-700 text-white hover:bg-blue-600'
                  : 'bg-blue-600 text-white hover:bg-blue-700'
              }`}
            >
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z"/>
              </svg>
              <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                Home
              </span>
            </button>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                window.location.href = '/auth';
              }}
              className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center gap-3 ${
                theme === 'dark'
                  ? 'bg-red-700 text-white hover:bg-red-600'
                  : 'bg-red-600 text-white hover:bg-red-700'
              }`}
            >
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z" clipRule="evenodd"/>
              </svg>
              <span className={`transition-opacity duration-300 ${sidebarOpen ? 'opacity-100' : 'opacity-0'}`}>
                Logout
              </span>
            </button>
          </div>
        </div>

        <div className={`${sidebarOpen ? 'ml-64' : 'ml-16'} flex-1 p-8 transition-all duration-300`}>
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                {activeTab === 'dashboard' && 'ðŸ  Employee Dashboard'}
                {activeTab === 'profile' && 'ðŸ‘¤ My Profile'}
                {activeTab === 'leave-request' && 'ðŸ“ Leave Request'}
                {activeTab === 'employees' && 'ðŸ‘¥ View Employees'}
                {activeTab === 'holidays' && 'ðŸ“… Holiday Calendar'}
                {activeTab === 'settings' && 'âš™ï¸ Settings'}
              </h1>
              <p className="text-gray-500">Welcome back, {user.name}!</p>
            </div>

          </div>

          {/* Dashboard */}
          {activeTab === 'dashboard' && (
            <div className="space-y-8">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {[
                  { title: 'Available Leaves', value: (employeeProfile?.availableLeaves || 0).toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd"/></svg>, color: 'bg-green-500' },
                  { title: 'Pending Requests', value: (Array.isArray(myLeaveRequests) ? myLeaveRequests.filter(req => req.status === 'pending').length : 0).toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd"/></svg>, color: 'bg-yellow-500' },
                  { title: 'Approved Leaves', value: (Array.isArray(myLeaveRequests) ? myLeaveRequests.filter(req => req.status === 'approved').length : 0).toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd"/></svg>, color: 'bg-blue-500' },
                  { title: 'Total Employees', value: (Array.isArray(employees) ? employees.length : 0).toString(), icon: <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20"><path d="M13 6a3 3 0 11-6 0 3 3 0 016 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3zM6 8a2 2 0 11-4 0 2 2 0 014 0zM16 18v-3a5.972 5.972 0 00-.75-2.906A3.005 3.005 0 0119 15v3h-3zM4.75 12.094A5.973 5.973 0 004 15v3H1v-3a3 3 0 013.75-2.906z"/></svg>, color: 'bg-purple-500' }
                ].map((stat, index) => (
                  <div key={index} className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-300`}>
                    <div className="flex items-center justify-between">
                      <div>
                        <p className={`text-sm mb-1 ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>{stat.title}</p>
                        <p className={`text-3xl font-bold ${theme === 'dark' ? 'text-white' : 'text-gray-800'}`}>{stat.value}</p>
                      </div>
                      <div className={`${stat.color} p-3 rounded-full text-white`}>
                        {stat.icon}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
                <h2 className="text-2xl font-bold mb-4">Quick Actions</h2>
                <div className="flex space-x-4">
                  <button onClick={() => setActiveTab('leave-request')} className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                    Request Leave
                  </button>
                  <button onClick={() => setActiveTab('profile')} className="px-6 py-3 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50 transition-colors">
                    View Profile
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Profile */}
          {activeTab === 'profile' && employeeProfile && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">My Profile</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Name</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{employeeProfile.name}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Employee ID</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{employeeProfile.employeeId}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Email</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{employeeProfile.workEmail}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Department</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{employeeProfile.department}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Date of Birth</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{new Date(employeeProfile.dateOfBirth).toLocaleDateString()}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Date of Joining</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{new Date(employeeProfile.dateOfJoining).toLocaleDateString()}</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Available Leaves</label>
                  <p className={`p-4 rounded-lg border font-semibold ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-green-400' : 'bg-gray-50 border-gray-200 text-green-600'}`}>{employeeProfile.availableLeaves} days</p>
                </div>
                <div>
                  <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Position</label>
                  <p className={`p-4 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'}`}>{employeeProfile.position}</p>
                </div>
              </div>
            </div>
          )}

          {/* Leave Request */}
          {activeTab === 'leave-request' && (
            <div className="space-y-8">
              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
                <h2 className="text-2xl font-semibold mb-6 text-blue-600">Submit Leave Request</h2>
                <form onSubmit={handleLeaveSubmit} className="grid grid-cols-2 gap-6">
                  <div>
                    <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Leave Type</label>
                    <select
                      value={leaveFormData.leaveType}
                      onChange={(e) => setLeaveFormData({...leaveFormData, leaveType: e.target.value})}
                      className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900'}`}
                    >
                      <option value="casual">Casual Leave</option>
                      <option value="sick">Sick Leave</option>
                      <option value="annual">Annual Leave</option>
                      <option value="emergency">Emergency Leave</option>
                    </select>
                  </div>
                  <div>
                    <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>From Date</label>
                    <input
                      type="date"
                      value={leaveFormData.fromDate}
                      onChange={(e) => setLeaveFormData({...leaveFormData, fromDate: e.target.value})}
                      className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900'}`}
                      required
                    />
                  </div>
                  <div>
                    <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>To Date</label>
                    <input
                      type="date"
                      value={leaveFormData.toDate}
                      onChange={(e) => setLeaveFormData({...leaveFormData, toDate: e.target.value})}
                      className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900'}`}
                      required
                    />
                  </div>
                  <div className="col-span-2">
                    <label className={`block text-sm font-medium mb-2 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-700'}`}>Reason</label>
                    <textarea
                      value={leaveFormData.reason}
                      onChange={(e) => setLeaveFormData({...leaveFormData, reason: e.target.value})}
                      className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-none ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'}`}
                      rows="3"
                      placeholder="Please provide a reason for your leave request..."
                      required
                    />
                  </div>
                  <div className="col-span-2">
                    <button type="submit" className="px-8 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 font-medium">
                      Submit Leave Request
                    </button>
                  </div>
                </form>
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
                <h2 className="text-2xl font-semibold mb-6 text-blue-600">My Leave Requests</h2>
                <div className="overflow-x-auto">
                  <table className="w-full border-collapse border border-gray-300">
                    <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                      <tr>
                        <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Leave Type</th>
                        <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>From Date</th>
                        <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>To Date</th>
                        <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Reason</th>
                        <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {myLeaveRequests.length > 0 ? myLeaveRequests.map((request) => (
                        <tr key={request._id} className={`${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-50'}`}>
                          <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{request.leaveType}</td>
                          <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{new Date(request.fromDate).toLocaleDateString()}</td>
                          <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{new Date(request.toDate).toLocaleDateString()}</td>
                          <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{request.reason}</td>
                          <td className={`border border-gray-300 p-4`}>
                            <span className={`px-2 py-1 rounded text-sm ${
                              request.status === 'approved'
                                ? 'bg-green-100 text-green-800'
                                : request.status === 'rejected'
                                ? 'bg-red-100 text-red-800'
                                : 'bg-yellow-100 text-yellow-800'
                            }`}>
                              {request.status}
                            </span>
                          </td>
                        </tr>
                      )) : (
                        <tr>
                          <td colSpan="5" className={`border border-gray-300 p-4 text-center ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>
                            No leave requests found
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          {/* View Employees */}
          {activeTab === 'employees' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">All Employees</h2>

              {/* Search and Filter */}
              <div className="mb-6 flex gap-4">
                <input
                  type="text"
                  placeholder="Search by name or employee ID"
                  value={employeeSearchQuery}
                  onChange={(e) => setEmployeeSearchQuery(e.target.value)}
                  className={`p-3 border rounded-lg flex-1 ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900'}`}
                />
                <select
                  value={departmentFilter}
                  onChange={(e) => setDepartmentFilter(e.target.value)}
                  className={`p-3 border rounded-lg w-48 ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900'}`}
                >
                  <option value="">All Departments</option>
                  {[...new Set(employees.map(emp => emp.department).filter(Boolean))].map(dept => (
                    <option key={dept} value={dept}>{dept}</option>
                  ))}
                </select>
              </div>

              <div className="overflow-x-auto">
                <table className="w-full border-collapse border border-gray-300">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Name</th>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Employee ID</th>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Department</th>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Position</th>
                    </tr>
                  </thead>
                  <tbody>
                    {employees.filter(emp => {
                      const matchesSearch = !employeeSearchQuery || 
                        emp.name?.toLowerCase().includes(employeeSearchQuery.toLowerCase()) ||
                        emp.employeeId?.toLowerCase().includes(employeeSearchQuery.toLowerCase());
                      const matchesDepartment = !departmentFilter || emp.department === departmentFilter;
                      return matchesSearch && matchesDepartment;
                    }).length > 0 ? employees.filter(emp => {
                      const matchesSearch = !employeeSearchQuery || 
                        emp.name?.toLowerCase().includes(employeeSearchQuery.toLowerCase()) ||
                        emp.employeeId?.toLowerCase().includes(employeeSearchQuery.toLowerCase());
                      const matchesDepartment = !departmentFilter || emp.department === departmentFilter;
                      return matchesSearch && matchesDepartment;
                    }).map((employee) => (
                      <tr key={employee._id} className={`${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-50'}`}>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{employee.name}</td>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{employee.employeeId}</td>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{employee.department}</td>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{employee.position}</td>
                      </tr>
                    )) : (
                      <tr>
                        <td colSpan="4" className={`border border-gray-300 p-4 text-center ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>
                          No employees found matching your search
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Holiday Calendar */}
          {activeTab === 'holidays' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">Holiday Calendar</h2>
              <div className="overflow-x-auto">
                <table className="w-full border-collapse border border-gray-300">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Holiday Name</th>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Date</th>
                      <th className={`border border-gray-300 p-4 text-left font-semibold ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Day</th>
                    </tr>
                  </thead>
                  <tbody>
                    {holidays.length > 0 ? holidays.map((holiday) => (
                      <tr key={holiday._id} className={`${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-50'}`}>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{holiday.name}</td>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{new Date(holiday.date).toLocaleDateString()}</td>
                        <td className={`border border-gray-300 p-4 ${theme === 'dark' ? 'text-gray-200' : 'text-gray-900'}`}>{new Date(holiday.date).toLocaleDateString('en-US', { weekday: 'long' })}</td>
                      </tr>
                    )) : (
                      <tr>
                        <td colSpan="3" className={`border border-gray-300 p-4 text-center ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>
                          No holidays found
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Settings */}
          {activeTab === 'settings' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
              <h2 className="text-2xl font-semibold mb-6 text-blue-600">Settings</h2>
              <div className="space-y-4">
                <div className={`flex items-center justify-between p-4 border rounded-lg ${theme === 'dark' ? 'border-gray-600' : 'border-gray-300'}`}>
                  <div>
                    <h3 className={`font-medium ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>Theme</h3>
                    <p className={`text-sm ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'}`}>Switch between light and dark mode</p>
                  </div>
                  <button
                    onClick={toggleTheme}
                    className={`px-4 py-2 rounded-lg transition-colors ${
                      theme === 'dark'
                        ? 'bg-blue-600 text-white hover:bg-blue-700'
                        : 'bg-blue-600 text-white hover:bg-blue-700'
                    }`}
                  >
                    {theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EmployeeDashboard;
