  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      // Scroll to first error field
      const firstErrorField = Object.keys(formErrors)[0];
      if (firstErrorField) {
        const element = document.querySelector(`[name="${firstErrorField}"]`);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          element.focus();
        }
      }
      return;
    }

    setIsSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token not found');
      }

      const url = editingEmployee 
        ? `http://localhost:5002/api/employees/${editingEmployee._id}`
        : 'http://localhost:5002/api/employees';
      const method = editingEmployee ? 'PUT' : 'POST';

      // Sanitize form data before sending
      const sanitizedData = {
        ...formData,
        name: formData.name.trim(),
        email: formData.email.trim().toLowerCase(),
        department: formData.department.trim(),
        position: formData.position.trim(),
        role: formData.role.trim(),
        phone: formData.phone?.trim() || '',
        address: formData.address?.trim() || ''
      };

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(sanitizedData),
      });

      const data = await response.json();

      if (response.ok) {
        await fetchEmployees();
        resetForm();
        alert(`Employee ${editingEmployee ? 'updated' : 'added'} successfully!`);
      } else {
        // Handle specific server errors
        if (response.status === 400) {
          alert(data.message || 'Invalid data provided. Please check your inputs.');
        } else if (response.status === 401) {
          alert('Authentication failed. Please login again.');
          localStorage.removeItem('token');
          window.location.href = '/auth';
        } else if (response.status === 409) {
          alert('Employee with this email or ID already exists.');
        } else {
          alert(data.message || 'Error saving employee. Please try again.');
        }
      }
    } catch (error) {
      console.error('Error saving employee:', error);
      if (error.message.includes('fetch')) {
        alert('Network error. Please check your connection and try again.');
      } else {
        alert('An unexpected error occurred. Please try again.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

