import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('overview');
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    personalEmail: '',
    employeeId: '',
    dateOfBirth: '',
    dateOfJoining: '',
    availableLeaves: 20,
    department: '',
    position: ''
  });
  const [message, setMessage] = useState('');
  const [credentials, setCredentials] = useState(null);
  const [departmentFilter, setDepartmentFilter] = useState('all');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user') || '{}');

      if (!token) {
        setMessage('No authentication token found');
        return;
      }

      if (user.role !== 'admin') {
        setMessage('Access denied. Admin role required.');
        return;
      }

      const response = await axios.get('http://localhost:5001/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEmployees(response.data);
      setMessage('');

    } catch (error) {
      console.error('Fetch employees error:', error);
      setMessage('Unable to load employees at this time.');
    }
  };

  const handleCreate = () => {
    setFormData({
      name: '',
      personalEmail: '',
      employeeId: '',
      dateOfBirth: '',
      dateOfJoining: '',
      availableLeaves: 20,
      department: '',
      position: ''
    });
    setIsCreating(true);
    setCredentials(null);
  };

  const handleEdit = (employee) => {
    setSelectedEmployee(employee);
    setFormData({
      name: employee.name,
      personalEmail: employee.personalEmail,
      employeeId: employee.employeeId,
      dateOfBirth: employee.dateOfBirth?.split('T')[0] || '',
      dateOfJoining: employee.dateOfJoining?.split('T')[0] || '',
      availableLeaves: employee.availableLeaves,
      department: employee.department || '',
      position: employee.position || ''
    });
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      if (isCreating) {
        const response = await axios.post('http://localhost:5001/api/admin/employees', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setCredentials(response.data.credentials);
        setMessage('Employee created successfully');
        setIsCreating(false);
      } else {
        await axios.put(`http://localhost:5001/api/admin/employees/${selectedEmployee._id}`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setMessage('Employee updated successfully');
        setIsEditing(false);
        setSelectedEmployee(null);
      }

      fetchEmployees();
    } catch (error) {
      setMessage(error.response?.data?.message || 'Operation failed');
    }
  };

  const handleDelete = async (employee) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5001/api/admin/employees/${employee._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setMessage('Employee deleted successfully');
        fetchEmployees();
      } catch (error) {
        setMessage(error.response?.data?.message || 'Delete failed');
      }
    }
  };


            <form onSubmit={handleSubmit} style={{display: 'grid', gap: '1.5rem'}}>
              <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem'}}>
                <div>
                  <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#34495e', marginBottom: '0.5rem'}}>
                    Name
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid #bdc3c7',
                      borderRadius: '6px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                    required
                  />
                </div>

                <div>
                  <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#34495e', marginBottom: '0.5rem'}}>
                    Personal Email
                  </label>
                  <input
                    type="email"
                    name="personalEmail"
                    value={formData.personalEmail}
                    onChange={handleChange}
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid #bdc3c7',
                      borderRadius: '6px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                    required
                  />
                </div>
              </div>

              <div>
                <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#34495e', marginBottom: '0.5rem'}}>
                  Employee ID
                </label>
                <input
                  type="text"
                  name="employeeId"
                  value={formData.employeeId}
                  onChange={handleChange}
                  disabled={!isCreating}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '1px solid #bdc3c7',
                    borderRadius: '6px',
                    fontSize: '0.875rem',
                    outline: 'none',
                    backgroundColor: !isCreating ? '#f8f9fa' : 'white'
                  }}
                  required
                />
              </div>

              <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem'}}>
                <div>
                  <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#34495e', marginBottom: '0.5rem'}}>
                    Date of Birth
                  </label>
                  <input
                    type="date"
                    name="dateOfBirth"
                    value={formData.dateOfBirth}
                    onChange={handleChange}
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid #bdc3c7',
                      borderRadius: '6px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                    required
                  />
                </div>

                <div>
                  <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#34495e', marginBottom: '0.5rem'}}>
                    Date of Joining
