import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile.jsx';
import AdminPanel from './AdminPanel.jsx';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [sidebarOpen, setSidebarOpen] = useState(window.innerWidth >= 768);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState({
    totalEmployees: 0,
    activeEmployees: 0,
    pendingLeaves: 0,
    holidays: 0
  });
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
        if (JSON.parse(userData).role === 'admin') {
          fetchDashboardData();
        }
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }
    setLoading(false);
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      const employees = response.data.filter(u => u.role === 'employee');
      setDashboardData({
        totalEmployees: employees.length,
        activeEmployees: employees.filter(u => u.hasProfile).length,
        pendingLeaves: 0, // Will be updated when leave system is implemented
        holidays: 0 // Will be updated when holiday system is implemented
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 768) {
        setMobileMenuOpen(false);
        setSidebarOpen(true);
      } else {
        setSidebarOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-md text-center max-w-md">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Access Denied</h2>
          <p className="text-gray-600 mb-6">Please log in to access your dashboard</p>
          <button 
            onClick={() => window.location.href = '/auth'} 
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition-colors"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  const sidebarItems = user.role === 'admin' ? [
    { id: 'overview', label: 'Overview', icon: 'ðŸ“Š' },
    { id: 'employees', label: 'Employee Management', icon: 'ðŸ‘¥' },
    { id: 'add-employee', label: 'Add Employee', icon: 'âž•' },
    { id: 'holiday-calendar', label: 'Holiday Calendar', icon: 'ðŸ“…' },
    { id: 'leave-requests', label: 'Leave Requests', icon: 'ðŸ“' },
    { id: 'settings', label: 'Settings', icon: 'âš™ï¸' }
  ] : [
    { id: 'overview', label: 'Overview', icon: 'ðŸ“Š' },
    { id: 'profile', label: 'My Profile', icon: 'ðŸ‘¤' },
    { id: 'tasks', label: 'Tasks', icon: 'âœ…' },
    { id: 'calendar', label: 'Calendar', icon: 'ðŸ“…' },
    { id: 'reports', label: 'Reports', icon: 'ðŸ“ˆ' },
    { id: 'settings', label: 'Settings', icon: 'âš™ï¸' }
  ];

  return (
    <div className={`min-h-screen flex relative ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
      {/* Mobile Overlay */}
      {mobileMenuOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-20 md:hidden"
          onClick={() => setMobileMenuOpen(false)}
        />
      )}
      {/* Sidebar */}
      <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg transition-all duration-300 flex flex-col z-30 ${
        sidebarOpen ? 'w-64' : 'w-16'
      } ${
        mobileMenuOpen ? 'fixed inset-y-0 left-0 md:relative' : 'hidden md:flex'
      }`}>
        <div className="p-4">
          <div className="flex items-center justify-between">
            <div className={`flex items-center space-x-3 ${!sidebarOpen && 'justify-center'}`}>
              <div className="bg-gradient-to-r from-blue-500 to-indigo-600 p-2 rounded-lg">
                <span className="text-white font-bold text-lg">CZ</span>
              </div>
              {sidebarOpen && (
                <span className="text-xl font-bold text-gray-800">CzarCore</span>
              )}
            </div>
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="hidden md:block p-2 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <span className="text-gray-600">{sidebarOpen ? 'â†' : 'â†’'}</span>
            </button>
            <button
              onClick={() => setMobileMenuOpen(false)}
              className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <span className="text-gray-600">âœ•</span>
            </button>
          </div>
        </div>

        <nav className="mt-8">
          {sidebarItems.map((item) => (
            <button
              key={item.id}
              onClick={() => {
                setActiveTab(item.id);
                setMobileMenuOpen(false);
              }}
              className={`w-full flex items-center px-4 py-3 text-left hover:bg-blue-50 transition-colors ${
                activeTab === item.id ? 'bg-blue-50 border-r-4 border-indigo-600 text-indigo-600' : 'text-gray-700'
              }`}
            >
              <span className="text-xl mr-3">{item.icon}</span>
              {sidebarOpen && <span className="font-medium">{item.label}</span>}
            </button>
          ))}
        </nav>

        <div className="mt-auto p-4 space-y-2">
          {sidebarOpen && (
            <button
              onClick={() => setDarkMode(!darkMode)}
              className={`w-full flex items-center justify-center px-4 py-3 transition-colors rounded-lg ${
                darkMode ? 'text-yellow-400 hover:bg-gray-700' : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <span className="text-xl mr-3">{darkMode ? 'â˜€ï¸' : 'ðŸŒ™'}</span>
              <span className="font-medium">{darkMode ? 'Light Mode' : 'Dark Mode'}</span>
            </button>
          )}
          <button
            onClick={handleLogout}
            className={`w-full flex items-center justify-center px-4 py-3 transition-colors rounded-lg ${
              darkMode ? 'text-red-400 hover:bg-gray-700' : 'text-indigo-600 hover:bg-indigo-50'
            }`}
          >
            <span className="text-xl mr-3">ðŸšª</span>
            {sidebarOpen && <span className="font-medium">Logout</span>}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Mobile Header */}
        <header className="md:hidden bg-white shadow-sm border-b">
          <div className="px-4 py-4">
